css:
@-moz-document regexp('http://www.ruanyifeng.com/blog/[0-9]*/[0-9]*/.*.html') {
/* 隐藏header */
#header {
    display: none !important;
}

/* 隐藏google_search */
#google_search {
    display: none !important;
}

/* 隐藏feed_icon */
#feed_icon {
    display: none !important;
}

/* 隐藏分享按钮 */
#share_button {
    display: none !important;
}

/* 隐藏meta信息 */
.asset-meta {
    display: none !important;
}

/* 隐藏header */
.asset-header {
    display: none !important;
}

/* 隐藏文档信息 */
.asset-footer {
    display: none !important;
}

/* 隐藏相关文章 */
#related_entries {
    display: none !important;
}

/* 因为元素已经使用了display: inline-block ! important; */
/* 所以这里使用绝对定位来移除元素 */
#sup-post-2 {
    position: absolute;
    left: -9999px;
    top: -9999px;
}

/* 隐藏广告 */
#gd1 {
    display: none !important;
}

/* 隐藏评论 */
#comments {
    display: none !important;
}
}
