css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

  #main-window {

    background-color: var(--theme-accent-background) !important;

  }



#TabsToolbar {
  
    background: transparent;
    border-width: 0px !important;
    border-top-width: 0px !important;
    
}

#TabsToolbar .tabs-newtab-button {
    border-width: 0px  !important;
    width: auto !important;
    margin: 0px !important;
    padding: 0px 8px !important;
    background: rgba(255,255,255,.0) !important;
    transition: all .11s !important;
    opacity: .3 !important;
}

#TabsToolbar .tabs-newtab-button:hover {
background-color: red !important;
}

#TabsToolbar tab:not([selected] ) {
      color: rgba(0,0,0,.4) !important;
}


#TabsToolbar tab:not([selected] ) image {
      opacity: 0.5 !important;
}

#TabsToolbar tab:not([selected]):hover {
     background: rgba(255,255,255,.5) !important;
     color: rgba(0,0,0,.9) !important;
}


#TabsToolbar tab[selected] {
    background: rgba(255,255,255,.9) !important;
}


#TabsToolbar .tab-background {
    margin: 0 !important;
    opacity: 0;
}

    /* hide tab close button */

#TabsToolbar  tab[selected]:not([pinned]):hover image {
       visibility: collapse;
}

#TabsToolbar  tab:not([pinned]):hover toolbarbutton image {
      visibility: visible !important; 
}




#TabsToolbar tab:not([pinned]) toolbarbutton image {
      visibility: collapse;
      margin-left: -5px !important;
      margin-right: 7px !important;
      display: -moz-box !important; 

    }





    /* Move close tab button on left */

#TabsToolbar  tab   .tab-text { 
    -moz-box-ordinal-group: 2 !important; 
    
}
