css:
@-moz-document domain("jmty.jp") {
/* Ad */
#adsense,
[class*="adsense"],
[class^="me_pc_"],
/* Sidebar */
#articles_sub_list,
#top_right,
.show_right,
/* Others */
#alert_box
{ display: none !important; }

/* Layout */
#articles_main_list,
#top_left,
#top_categorys,
.news_other,
.news_other ul,
.show_left,
.article_recommend {
  width: auto;
  float: none;
}

.list_sale > div:not(.position_relative) {
  display: none;
}
.list_sale li {
  height: 111px;
}
.list_sale li a.image,
.list_sale li a.image img {
  width: 111px;
  height: 111px;
}
.list_sale li h3 {
  margin-left: 120px;
  font-size: 18px;
}
.list_sale li .padding_right60 {
  margin-left: 120px;
}
.list_sale li .padding_right60 span.items {
  margin-bottom: 10px;
}
.list_sale li .padding_right60 span.detail {
  width: 600px;
  font-size: 12px;
  line-height: 1.4;
}

#top_left .top_main_contents_width {
  width: auto;
}
#top_categorys div.leftbox,
#top_categorys div.rightbox {
  width: 300px;
  padding: 15px;
}

.show_img_space,
.article_texts {
  width: 700px !important;
}

.show_img_space .image_block,
.show_img_space .image_block img {
  width: 200px;
  height: 200px;
}
}
