css:
@-moz-document regexp(".*gitbooks.io.*") {
.page-inner,
.book-body,
.book-summary {
	background: #1a2835;
}
#book-search-input {
	background: #101e2d;
}
.book-summary ul.summary>li>a,
li.chapter>.articles>li>a {
	color: whitesmoke;
}
a.navigation.navigation-prev,
a.navigation.navigation-next {
	background: rgba(15, 30, 45, 0.94);
}
.book-header {
	background: hsl(253, 48%, 37%);
}
code,
.markdown-section code {
	color: black;
}
section.normal.markdown-section {
	color: whitesmoke;
}
}
