css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("https://twittrp.com/") {
  /* TwittRP Dark Mode Override */
  
body {
  background: #121820;
  color: #8e9fac;
}
.container .jumbotron {
  background: #212c3a;
  border: 1px solid #2e3d50;
}
h1, .container .jumbotron h1 {color: #dddee0;}
.list-group-item {
  background: #212c3a;
  color: #bccedc;
  border: 1px solid #2e3d50;
  transition: 0.4s;
	-webkit-transition: 0.4s;
}
.form-control {
  background: #212c3a;
  border: 1px solid #2e3d50;
  color: #cbdae6;
}
.mention {
  background: #212c3a;
  transition: 0.5s;
	-webkit-transition: 0.5s;
  padding: 10px 10px 10px 15px;
  margin-left: 50px;
  margin-bottom: 5px;
  border: 1px solid #2e3d50;
  -webkit-border-radius: 6px;
  border-radius: 6px;
}
.mention:hover{
  border: 1px solid #5f7188;
}
.mention-avatar img{
  -webkit-border-radius: 5px;
  border-radius: 5px;
  transition: 0.5s;
	-webkit-transition: 0.5s;
  border: 1px solid #2e3d50;
  opacity: 0.7;
}
.mention-avatar img:hover {opacity: 1;}
#selected-mention h3{
  margin-left: 50px;
  color: #dddee0;
}
.btn-warning{
  border: 1px solid black;
  background: -webkit-linear-gradient(90deg,  #090c10 4%, #11171f 100%);
  padding: 5px;
  transition: 0.4s;
}
.btn-warning:hover,.btn-warning:focus,.btn-warning:active,.btn-warning.active,.open .dropdown-toggle.btn-warning {
  border: 1px solid black;
  box-shadow: 0px 0px 6px #428bca;
}
.btn {transition:0.3s;}
#list-mentions .mention-action-reply-to:hover {color: #b5bcca; text-decoration: none;}
.mention-user-screenname, .mention-separator, .mention-time, .mention-action-reply-to {color:#5d757f;}
.mention-user-displayname {color:#dddee0;}
.mention-text p {color: #b5bcca;}
}
