css:
@-moz-document domain("cs.rin.ru") {

.row1,row2,.row2,.row5 {
    background-color: #26292c;
    padding: 2px 2px 1px;
}
.bodyline {
    background: #26292c;
    border: 1px solid #575b5f;
}
a:visited {
    color: #00b9d7;
    text-decoration: none;
}    
.postbody {
    font-size: 1.3em;
    line-height: 1.4em;
    font-family: monospace;
    color: #00b9d7;
}
a.breadcrumbs {
    color: #228dc5;
}
.postauthor {
    color: #a6e22d;
    font-size: 1.1em;
}
a:link {
    color: #00b9d7;
    text-decoration: none;
}
a.topictitle:visited,a:visited,a:visited {
    color: #f92672;
    text-decoration: none;
}
input {
    color: #2a2a2a;
    font-family: monospace;
    font-size: 1.1em;
    font-weight: normal;
    padding: 1px;
    border: none;
    background-color: #e6db74;
}
body {
    font-family: monospace;
    color: #00b9d7;
    background-color: #26292c;
    font-size: 73.5%;
    margin: 0px 10px 10px 10px;
}
.post {
    background-color: #e6db74;
    border-style: solid;
    border-width: 1px;
}
.btnmain {
    font-weight: bold;
    background-color: #e6db74;
    border: 1px solid #0E0E0E;
    cursor: pointer;
    padding: 1px 5px;
    font-size: 1.1em;
}
.copyright {
    color: #e6db74;
    font-weight: normal;
    font-family: "Lucida Grande", Verdana, Arial, Helvetica, sans-serif;
}
input:hover {
	color: #000;
}
.tablebg {
    background-color: #5a5c5d;
}
td.profile,.gensmall,.tablebg {
    font-size: 1.2em;
}
}
