css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://keskustelu.plaza.fi/muropaketti/bbs/){
/* Mainokset pois */
div[id="masthead"],
div[id="sidebar-ads"],
div[id="topadvertisements"],
div[style="padding: 3px;"],
dt[style="border-top: 1px solid gray; margin-top: 10px; padding-top: 10px;"]
{ display: none !important; }

/* Sivun leveys koko nÃ¤ytÃ¶lle */
div[id="content"]
{ width: 100% !important; }

/* Tiiviimpi signature */
#bbs-wrapper .signature { font-size: 11px !important; color: #8F8F8F !important; margin-top: 15px !important; width: 70% !important; border-top: none !important; margin-left: auto !important; text-align: right !important;}

/* Tiiviimi sivunumerointi */
div[class="threadpages"]
{ color: #FFF !important;  margin: -5px 0px 0px 0px !important; }

/* Tiiviimpi aikataulu */
td[class="last-message-info"]
{ padding: 0px !important; background: #D8DDE4 !important; }
td[class="threadtitle"]
{ background: #fefefe !important;  }

/* KevyeemmÃ¤t reunat */
table {	border-collapse: collapse !important; }
#bbs-wrapper blockquote { border-style: none !important; margin: 0 !important; }
#bbs-wrapper blockquote blockquote { border-style: none !important; }
#bbs-wrapper blockquote blockquote blockquote { border-style: none !important; }
#bbs-wrapper blockquote blockquote blockquote blockquote { border-style: none !important; background: #D8DDE4 !important; }
#bbs-wrapper blockquote blockquote blockquote blockquote blockquote { border-style: none !important; background: #E4E8ED !important; }
#bbs-wrapper blockquote blockquote blockquote blockquote blockquote blockquote { border-style: none !important; background: #F0F3F8 !important; }
#top-row td, #top-row th, #bottom-row td, #bottom-row th { border-style: none !important; }
td { border-color: #8F8F8F !important; padding: 2px !important; border-width: 1px !important; border-style: dotted !important;}

/* Tiiviimpi lainaus */
div[class="post"]
{ margin: 15px 0px 0px 0px !important; }
div[class="postfooter"]
{ height: 5px !important; margin-bottom: 0px !important; }
div[class="post-functions"]
{ height: 5px !important; margin-bottom: 0px !important; }
a[class="button-lainaa"]
{ height: 5px !important; width: 32px !important; background-image: url(data:image/gif;base64,R0lGODlhIAAFAIAAAAAAAP%2F%2F%2FyH5BAAAAAAALAAAAAAgAAUAAAIdRI5gtxuu0DOO2cfa1VPOiGFUBiqmx6Xpdm7umxYAOw%3D%3D) !important; }
a[class="button-muokkaa"]
{ height: 5px !important; width: 55px !important; background-image: url(data:image/gif;base64,R0lGODlhKgAFAIAAAAAAAP///yH5BAAAAAAALAAAAAAqAAUAAAIkRG6RibwI25HA1OiwmpZr1T3bd3EMWFaqN2btuLliCdMSibcFADs=) !important; }
a[class="button-poista"]
{ height: 5px !important; width: 31px !important; background-image: url(data:image/gif;base64,R0lGODlhHwAFAIAAAP///7YAACH5BAAAAAAALAAAAAAfAAUAAAIfjIFwGGnNlmqwsquShnlK201a+EgY6GypOH4Rh3VBAQA7) !important; }
a[class="button-lukitse"]
{ height: 5px !important; width: 34px !important; background-image: url(data:image/gif;base64,R0lGODlhIgAFAIAAAP///9STWCH5BAAAAAAALAAAAAAiAAUAAAIgDA6Wp82hEnwTuRWzRazvJXGalUER6Iyg6Jnkd60uVQAAOw==) !important; }

/* Ylareunan siistimista */
#bbs-wrapper #forum-header #logged dt { display: none !important; }

/* Alareunan siistimista */
div[id="footer"] { display: none !important; }

/* Uusi logo murolle */
div[id="forum-header"] { margin-top: 10px !important; }
#forum-header h1 {background-image: url(data:image/jpeg;base64,) !important; height: 44px !important; width: 194px !important; margin: 0px 140px -40px 30px !important; }

/* 11cm puhekuplat */
img[src="http://plaza.fi/s/img/new-messages_w-icon.gif"],
img[src="http://plaza.fi/s/img/locked-topic_w-icon.gif"],
img[src="http://plaza.fi/s/img/no-new-messages_w-icon.gif"]
{ width: 16px !important; height: 15px !important; margin: 5px 10px 10px 5px !important; }
}
