css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http://www.youtube-mp3.org.*") {

body {
  background-color:black;
  background-image:
  radial-gradient(white, rgba(0, 128, 255,.2) 2px, transparent 40px),
  radial-gradient(white, rgba(0, 128, 255,.15) 1px, transparent 30px),
  radial-gradient(white, rgba(0, 128, 255,.1) 2px, transparent 40px),
  radial-gradient(rgba(0, 128, 255,.4), rgba(0, 128, 255,.1) 2px, transparent 30px) !important;
  background-size: 550px 550px, 350px 350px, 250px 250px, 150px 150px !important; 
  background-position: 0 0, 40px 60px, 130px 270px, 70px 100px !important;
}
 
#inner {}
  
img {
  float: left !important;
  max-width: 290px !important;
  width: 100% !important;
  height: auto !important; 
  display: block !important;
  margin-left: auto !important;
  margin-right: auto !important;
  }  
  
#image {
    -webkit-filter: contrast(200%); /* Safari 6.0 - 9.0 */
    filter: contrast(200%);
}

#submit-form {
    margin-top: 5% !important;
  }  
  
  #dl_link a {
    color: rgb(0, 128, 255) !important;  
  }
  
#youtube-url {
  width: 100% !important;
  color: rgb(255, 255, 255) !important;
  background-color: rgba(10,10,10,.6) !important;
  background-image: repeating-linear-gradient(-45deg, rgba(0,128,255,0.1), rgba(0,128,255,0.1) 1px, transparent 1px, transparent 4px)!important;
  border: solid 1px rgb(0, 128, 255) !important;  
  -webkit-box-shadow:0 0 5px rgb(0, 128, 255) !important;   
  -moz-box-shadow: 0 0 5px rgb(0, 128, 255) !important;  
  box-shadow:0 0 5px rgb(0, 128, 255) !important;
  font-family: monospace;
  font-size: 1.5em  !important;
  letter-spacing: 0.125em; 
  }
  
.error {
    border: solid 1px rgb(255, 0, 0) !important; 
  }  
  
#error_text {
    color: rgb(255, 255, 255) !important;
  }
  
#error_text a {
    color: rgb(0, 128, 255) !important;
  }
  
  
  #content {
    width: 80% !important;
    max-width: 100% !important;
  }
  
#content #inner {
  max-width: 100% !important;
  float: left !important;
  margin: auto !important;
  top: 0 !important;
  bottom: 0 !important;
  right: 0 !important;
  left: 0 !important;
}

#progress_info {
  width: 100.0% !important;
  max-width: 100.0% !important;
  background-color: rgba(10,10,10,.6) !important;
  background-image: repeating-linear-gradient(-45deg, rgba(0,128,255,0.1), rgba(0,128,255,0.1) 1px, transparent 1px, transparent 4px)!important;
  font-size: 1em !important;  
  line-height: none !important;
  -webkit-box-shadow:0 0 5px rgb(0, 128, 255) !important;   
  -moz-box-shadow: 0 0 5px rgb(0, 128, 255) !important;  
  box-shadow:0 0 5px rgb(0, 128, 255) !important;
  
  }

.success {
    background-color: rgb(20, 20, 20) !important;
    border: solid 1px rgb(0, 128, 255) !important;  
  }

#details {
  color:   rgb(255, 255, 255) !important;
  } 

#details a {
    text-transform: uppercase !important;
    text-decoration: none !important;
  }  
  
input[type=text] {
    color: rgb(255, 255, 255) !important;
    padding:5px !important; 
    background-color: rgb(20, 20, 20) !important;
    border: solid 1px rgb(0, 128, 255) !important;  

}

input[type=submit] {
    padding:5px 15px; 
    background-color: rgb(20, 20, 20) !important;
    border: solid 1px rgb(0, 128, 255) !important;  
    cursor:pointer; 
} 
  
#description {
  background-color: rgba(10,10,10,.6) !important;
  background-image: repeating-linear-gradient(-45deg, rgba(0,128,255,0.1), rgba(0,128,255,0.1) 1px, transparent 1px, transparent 4px)!important;
  border: solid 1px rgb(0, 128, 255) !important; 
  -webkit-box-shadow:0 0 5px rgb(0, 128, 255) !important;   
  -moz-box-shadow: 0 0 5px rgb(0, 128, 255) !important;  
  box-shadow:0 0 5px rgb(0, 128, 255) !important;
  }
  
  #description p {
  color: rgb(255, 255, 255) !important;
  }
  
#description h2 {
  color: rgb(255, 255, 255) !important;
  text-align: left;
  background: #e7eeff;
  border: 0 none !important;
  margin-top: 0 !important;
  }
 
  h2 {
    color: rgb(255, 255, 255) !important;
    background-color: rgba(0, 0, 0, 0) !important;
    border: 0 none !important;
    font-size: 1.2em !important;
  }  
  
p {
  font-size: 1.2em;
  margin-top: 1% !important;
  margin-bottom: 1% !important;
  margin-right: 1% !important;
  margin-left: 1% !important;
  }  
 
#btns {
  text-transform: uppercase !important;
  }  
  
#submit {
  position: relative !important;
  max-width: 300px !important;
  width: 100% !important; 
  height: 300px !important; 
  margin: 0 !important;
  padding: 0 !important;
  color: rgb(255, 255, 255) !important;
  background-color: rgba(10,10,10,.6) !important;
  background-image: repeating-linear-gradient(-45deg, rgba(0,128,255,0.1), rgba(0,128,255,0.1) 1px, transparent 1px, transparent 4px)!important;
  text-transform: uppercase !important;
  margin-top: 2% !important;
  margin-bottom: 2% !important;
  -webkit-box-shadow:0 0 5px rgb(0, 128, 255) !important;   
  -moz-box-shadow: 0 0 5px rgb(0, 128, 255) !important;  
  box-shadow:0 0 5px rgb(0, 128, 255) !important;
  -webkit-transition: border-radius !important;
  -webkit-transition-duration: 0.5s !important;  
  
  -webkit-transition-timing-function: linear !important;
}
  
  
#submit:hover {
    border-radius: 100% !important;
    background-color: rgba(10,10,10,.6) !important;
    background-image: repeating-linear-gradient(-45deg, rgba(0,128,255,0.2), rgba(0,128,255,0.2) 1px, transparent 1px, transparent 4px)!important;
    -webkit-animation: neon2 1.5s ease-in-out infinite alternate !important;
    -moz-animation: neon2 1.5s ease-in-out infinite alternate !important;
    animation: neon2 1.5s ease-in-out infinite alternate !important;
    -webkit-box-shadow:0 0 20px rgb(0, 128, 255) !important;   
    -moz-box-shadow: 0 0 20px rgb(0, 128, 255) !important;  
    box-shadow:0 0 20px rgb(0, 128, 255) !important;   
  }
  

#submit:focus {
    border-radius: 100% !important;
  }


#form #submit {
  font-weight: bold !important;
  font-size: 1em !important;
  }


#footer {
  font-size: 1em !important;
  border-top: solid 1px rgb(0, 128, 255) !important;
  margin-top: 2% !important;
  }
  
#footer a {
  color: rgb(255, 255, 255) !important;
  font-size: 1em !important;
  text-transform: uppercase !important;
  }
}
