css:
@-moz-document domain("klavogonki.ru") {
.ownbanner-back,
/* #play-right, */
#playads, #playads_dummy, #params, #play-right > #statistics, #invite,
.feedback, .indexstats-block,
.firstrow, #footer td[align="left"],
/* #gamelist > div:nth-child(1), */
/* .warn, */
.threecol233 .col1,
.imobilco-link,
#typeblock #bookinfo #book th,
.imobilco-container,
#typeblock #bookinfo #again,
#index #live .grad,
#typeblock #bookinfo .bar,
#footer div:first-child, .columns .right-col,
#keyboard_cont,
#reformal_tab,
#racing_time,
/* #chat-fixed-placeholder, */
[src="/img/smilies/firework.gif"],
[src="/img/smilies/confetti.gif"] {
	display: none;
}
body {
    background: repeating-linear-gradient(#0000ff, #0000ff 49.9%, #ffff00 50.1%, #ffff00 100%);
}
#play-right {
	min-width: 0;
    margin-left: 0;
}
.play-overall-table {
    position: relative;
}
#play-right #complexity-panel {
    position: absolute;
    top: 8px;
    right: -435px;
    opacity: 0.25;
    background-color: transparent !important;
}
#play-right #complexity-panel h4 {
	display: none;
}
#head, #content, #content_KE {
	margin: 10px 15px;
}
#head #logo {
	padding: 5px 0 5px 30px;
}
#head #logo img {
	height: 50px;
}
.login-block {
  position: absolute;
  right: 0px;
  top: 0px;
  color: darkgoldenrod;
  font-weight: bold;
  text-shadow: 0 0 15px white;
  padding: 3px 20px 3px 10px;
}
.login-block a span {
	border-bottom: 2px dotted goldenrod;
}
.login-block a {
  background-position: 10% 2px;
  background-color: rgba(0,0,0,0.7);
  border-radius: 10px;
  padding: 0px 10px 4px 30px;
  color: darkgoldenrod;
  box-shadow: 0 0 10px darkred;
  text-shadow: 0 0 5px black;
}
#index #live {
  background: #faf9ed;
  padding-bottom: 15px;
  border-radius: 15px;
}
#login-form {
  top: 25px;
  right: 5px;
}
#head .right .menu {
	margin-top: 0;
}
#content {
	padding: 10px 15px 0;
}
.gamelist-create {
	margin-bottom: 5px;
}
a {
  text-decoration: none;
}
#gamelist td.players{
	padding: 0px;
}
#index .secondrow {
	margin-top: 0px;
}
#toplist ul.mode li {
  background: rgba(0,0,0,0.1);
  padding: 3px 0;
  border-radius: 10px;
  text-align: center;
}
#toplist ul.mode li a:hover {
	text-decoration: none;
}
div#gamelist .item td {
	border-top: none;
}
#toplist ul.mode li.active {
  padding: 0 0 0 10px;
  left: -30px;
}
#typeblock #bookinfo #book td .author {
  margin: 0 20px;
  float: left;
  max-width: 300px;
  display: flex;
  width: 300px;
  height: 60px;
  align-items: center;
  flex-direction: row-reverse;
  text-align: center;
}
#typeblock #bookinfo a {
  margin: 0 5px;
  text-decoration: none;
  display: flex;
  align-items: center;
  flex-direction: row;
  height: 60px;
  width: 320px;
  font-size: 18px;
}
#typeblock #bookinfo div[style="color: black"] a, #typeblock #bookinfo .gametype-voc a{
  display: inline;
}
#typeblock #bookinfo #book td div:first-child {
	text-align: center;
}
.threecol233 .col2 {
	width: 58%;
  left: 0%;
}
.threecol233 .col3 {
  width: 40%;
  left: 2%;
}
#head, #typeblock, #footer table, #chat-fixed-placeholder #chat-container, #content, #content_KE, .gamelist-create .rc, #statistics, #players, #speedpanel {
  box-shadow: 0 0 20px gainsboro;
  border-radius: 12px;
  overflow: hidden;
}
#top {
	margin-bottom: 10px;
}
#footer table {
	background-color: #fafcf4;
  margin-top: 10px;
}
#footer td[align="right"] {
	padding: 5px 15px !important;
}
#footer {
	padding: 0 15px;
}
.play-wrapper {
	margin: 0px;
}
#chat-container {
	bottom: -2px !important;
}
.chat-hidden #chat-container {
	bottom: 0px !important;
}
#chat-fixed-placeholder #chat-container {
	border-radius: 15px 15px 0 0;
}
#gameloading {
  margin: 20px auto;
  border-radius: 15px;
  box-shadow: 0 0 15px gainsboro;
  overflow: hidden;
}
.fuel-root .fuel-content, .fuel-root .thankyou {
  left: 2%;
  width: 96%;
}
.fuel-root .thankyou {
  margin-top: -15px;
}
.columns .content-col {
  margin-right: 0%;
}
#content_KE {
	padding: 0 15px;
}
.chat .messages .time[style="background-color: yellow;"], .chat .messages .time[style="background-color: yellow;"] ~ * {
  color: black !important;
  background-color: gold !important;
  font-size: 11px;
}
#typeblock #bookinfo .name {
  max-width: 350px;
  text-align: center;
}
.vocview-block #toplist dt ul.mode li.active {
  width: 115%;
  padding: 0 20px 0 45px !important;
  margin-left: 10px;
}
.popup_message .content1 {
	top: calc(50% - (250px/2));
}
.popup_message label {
	left: 10px;
}
.stdpopup.popup_message .content1 {
	top: 15% !important;
}
#inputtext {
  outline: 0;
  border-width: 0;
  box-shadow: 0 0 15px darkgrey;
  padding: 5px;
  border-radius: 10px;
  width: 100%;
  box-sizing: border-box;
}
#inputtext[disabled] {
	text-align: center;
}
#typeblock #hiddentext
{
  border-radius: 15px;
  box-shadow: 0 0 15px darkgrey;
}
.name_content {
  width: 125px;
  margin-left: -10px;
}
#typetext.full .highlight {
/*   background-color: lightgoldenrodyellow; */
/*   color: black; */
  text-decoration: none;
/*   box-shadow: 0 0 10px gold; */
    color: #222222;
}
#typetext.full #beforefocus {
	color: #559955;
}
#typetext.full .highlight_error {
/*   color: black !important; */
  text-decoration: none;
/*   box-shadow: 0 0 10px red; */
}
#inputtext.error {
  color: black;
  background-color: white !important;
  box-shadow: 0 0 15px red;
}
#toplist ul.mode li.active span {
  text-shadow: 0 0 7px darkred;
  color: black;
}
.columns .left-col * {
  color: dimgray;
  transition: 0.3s;
}
#toplist ul.mode li:hover a {
  text-shadow: 0 0 7px darkred;
  color: black;
}
#toplist ul.mode li.active span:first-child:before, #toplist ul.mode li:hover a:first-child:before {
  content: "< ";
}

#toplist ul.mode li.active span:last-child:after, #toplist ul.mode li:hover a:last-child:after {
  content: " >";
}

.vocview-block #toplist dt ul.mode li.active span {
  padding-right: 30px;
}
.vocview-block #toplist dt ul.mode li.active {
  margin-left: 0px;
}

@keyframes arrows-left {
  0%   { content: ""; }
  25%  { content: "< "; }
  50%  { content: "<< "; }
  75%  { content: "< "; }
  100% { content: ""; }
}
@keyframes arrows-right {
  0%   { content: ""; }
  25%  { content: " >"; }
  50%  { content: " >>"; }
  75%  { content: " >"; }
  100% { content: ""; }
}
#toplist ul.mode li:hover a:first-child:before {
	animation-name: arrows-left;
  animation-duration: 2s;
  animation-iteration-count: infinite;
}
#toplist ul.mode li:hover a:last-child:after {
  animation-name: arrows-right;
  animation-duration: 2s;
  animation-iteration-count: infinite;
}

/*
@keyframes rotate-comp {
  0% { transform: rotate( 0deg ); }
  100% { transform: rotate( 5760deg ); }
}
#slow_timer[style] ~ #gamelist td.enter a.competition {
  animation-name: rotate-comp;
  animation-duration: 5s;
  animation-iteration-count: 1;
}
#slow_timer ~ #gamelist td.enter a.competition.notallowed {
	animation: none;
}

#chat-wrapper .smile, #chat-wrapper .insert-smile-btn img {
  animation-name: rotate-comp;
  animation-duration: 5s;
  animation-iteration-count: infinite;
}
*/

/* wait spinner
@keyframes rotate-comp {
  0% { transform: rotate( 0deg ); }
  100% { transform: rotate( 18000deg ); }
}
@keyframes rotate-comp-reverse {
  0% { transform: rotate( 0deg ); }
  100% { transform: rotate( -18000deg ); }
}
.minwidth_holyhack td:nth-child(1), .minwidth_holyhack td:nth-child(3) {
  height: 132px;
  width: 116px;
  overflow: hidden;
  display: block;
  animation-name: rotate-comp;
  animation-duration: 10s;
  animation-iteration-count: infinite;
  border-bottom: 30px solid white;
  margin: 15px 25px;
}
.minwidth_holyhack td:nth-child(3) {
    animation-name: rotate-comp-reverse;
}
.minwidth_holyhack td:nth-child(1) img, .minwidth_holyhack td:nth-child(3) img {
  margin-top: -2px;
  margin-left: -16px;
}
/* */

.warn {
  font-size: 0;
  position: absolute;
  top: 10px;
  right: 10px;
  background-color: white;
  border-radius: 0 10px 10px 0;
  border-left: 5px gray solid;
  box-sizing: border-box;
  height: 22px;
  width: 130px;
  background-position: 2px 1px;
}
.warn a {
	font-size: 14px;
}
.minwidth_container {
	position: relative;
}
#anim_speed_table {
  margin-right: 150px;
  background: white;
  border-radius: 10px 0 0 10px;
  border-right: 5px solid gray;
  padding: 1px 5px 1px 7px;
  display: block;
}

div[style="background-color: #f3f3f3; margin-top: -10px; margin-bottom: 10px"]{
  background: none !important;
  margin-top: -10px;
  margin-bottom: 10px;
  position: absolute;
  top: -52px;
  right: 0px;
}
div[style="background-color: #f3f3f3; margin-top: -10px; margin-bottom: 10px"] .r.tl,
div[style="background-color: #f3f3f3; margin-top: -10px; margin-bottom: 10px"] .r .tr,
div[style="background-color: #f3f3f3; margin-top: -10px; margin-bottom: 10px"] .r .bl,
div[style="background-color: #f3f3f3; margin-top: -10px; margin-bottom: 10px"] .r .br {
	background: none;
}
#slow_timer {
  position: absolute;
  top: 10px;
  right: 150px;
  font-size: 0;
  background-color: white;
  border-radius: 10px 0 0 10px;
  border-right: 5px solid gray;
  height: 22px;
  width: 120px;
  background-position: 93px 1px;
  padding: 2px 0 0 7px;
  text-transform: lowercase;
}
#slow_timer a {
  font-size: 14px;
}
[style="filter: blur(2px);"] {
	filter: blur(2px) grayscale(1) !important;
    text-transform: lowercase;
    display: none;
}
.dlg-dailytask-window .modal2-dialog .task-description .require .conditions li {
	position: relative;
}
.dlg-dailytask-window .modal2-dialog .task-description .play-btn-solid {
    position: absolute;
    top: 50%;
    margin-top: -10px;
    right: 0;
}
.dlg-dailytask-window .modal2-dialog .task-description li > [condition="condition"] {
    width: 80%;
    display: block;
}
.popup_message .btn {
	padding: 0;
    line-height: 30px;
}
#chat-title table .c span.new {
	background: none;
}
}

@-moz-document url-prefix("https://klavogonki.ru/g/") {
.minwidth_container {
  position: inherit;
}
/* #content {
    background: ghostwhite;
} */
}
