css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {
.svg .logo_homepage {
	background-image: url("https://www.google.com//images/branding/googlelogo/2x/googlelogo_color_272x92dp.png") !important;
	background-size: 249px 90px !important;
}
.logo_homepage {
    width: 300px;
}
.svg .header__logo {
	background-image: url("https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_150x54dp.png") !important;
	background-size: 60px 20px !important;
  }
.header__logo, .header__praise, .header__avatar {
    width: 56px;
    height: 36px;
}
	.tag-home__item {
	visibility: hidden !important;
	}
	.search__button:hover, .search__button:focus, .search:hover .search__button:focus, .search--header.has-text.search--hover .search__button:hover, .search--header.has-text.search--focus .search__button:hover, .search--home.has-text .search__button:focus, .search--home.has-text .search__button:hover {
    background-color: #4285f4;
    color: white;
	}
	.search:hover .search__button, .search__input:focus ~ .search__button, .search--header.has-text.search--hover .search__button, .search--header.has-text.search--focus .search__button, .search--home.has-text .search__button {
    background-color: #4285f4;
    color: white;
	}
	.onboarding-bottom-bathroomguy .onboarding-bottom-verticalstory-arrow {
    visibility: hidden;
	}
	.no-touch .logo_homepage:hover .logo_homepage__tt {
    -moz-transition-delay: 0.5s;
    -o-transition-delay: 0.5s;
    -webkit-transition-delay: 0.5s;
    transition-delay: 0.5s;
    visibility: hidden;
    opacity: 1;
	}
	.header-wrap--home {
    border-top: none;
    padding-top: 16px;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    width: 100%;
    z-index: 5;
	}
	.header-wrap {
    -moz-box-shadow: 0 1px 0 rgba(0,0,0,0.15);
    -webkit-box-shadow: 0 1px 0 rgba(0,0,0,0.15);
    box-shadow: 0 1px 0 rgba(0,0,0,0.15);
    -webkit-tab-highlight-color: transparent;
    background-color: #fff;
    padding-top: 16px;
    border-top: none;
    position: relative;
    z-index: 15;
	}
}
