@-moz-document domain("roblox.com") {
/* Created by TheLMGN */

/*[[txt]]*/

#navigation {
 	background-color: /*[[bc2]]*/   
}

.game-card-container {
    background-color: /*[[aux1]]*/
}

#game-detail-page {
    position:absolute;
    left:50%;
    margin-right:-50%;
    transform:translate(-50%,0%);
}
::-webkit-scrollbar {
    height: 12px;
    width: 15px;
    background-image: -webkit-radial-gradient(center, circle cover, #fff 0%, #fff 100%);
    opacity: 0.8;
    
}
::-webkit-scrollbar-thumb {
    background-image: -webkit-radial-gradient(center, circle cover, #dbdbdb 0%, #dbdbdb 100%);
    left: -10px;
}
::-webkit-scrollbar-corner {
    background: #A3C1F7;
}

footer { display:none;}

.notification-red {
    background-color: /*[[ac1]]*/;
}
}

@-moz-document domain("roblox.com") {
/* Created by 4reeeal https://userstyles.org/styles/129504/clean-roblox-alpha */

@keyframes transitionFade {
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}
@keyframes expand {
    0% {
        width: 30px;
        opacity: 0.2;
        background-color: transparent;
    }
    25% {
        width: 30px;
        opacity: 0.5
    }
    50% {
        width: 30px;
        opacity: 0.5;
        background-color: transparent;
    }
    100% {
        width: 120px;
        opacity: 1;
        background-color: /*[[bc1]]*/;
    }
}
@keyframes homescreenA {
    0% {
        opacity: 1;
    }
    75% {
        opacity: 1;
    }
    100% {
        opacity: 0;
    }
}
@keyframes homeone {
    0% {
        top: 100%;
        opacity: 0;
    }
    20% {
        top: 39%;
        opacity: 1;
    }
    25% {
        top: 40%;
    }
    80% {
        top: 40%;
    }
    100% {
        top: -500px;
        opacity: 1;
    }
}
@keyframes hometwo {
    0% {
        opacity: 0;
    }
    25% {
        opacity: 0;
    }
    35% {
        opacity: 1;
    }
    50% {
        opacity: 1;
    }
    70% {
        opacity: 1;
    }
    80% {
        opacity: 0;
    }
    100% {
        opacity: 0;
    }
}
@keyframes homethree {
    0% {
        margin-top: 90%;
        opacity: 1;
    }
    80% {
        margin-top: 90%;
        opacity: 1;
    }
    85% {
        opacity: 0;
    }
    95% {
        opacity: 0;
    }
    100% {
        margin-top: 0px;
        opacity: 1;
    }
}
body {
    background-color: /*[[bc1]]*/;
    animation-name: transitionFade;
    animation-duration: 0.25s;
}
#header {
    background-color: /*[[bc1]]*/;
}

.navbar-brand,
.icon-nav-menu,
rbx-nav-collapse {
    display: none;
}
#navigation ul .text-lead,
#navigation ul .rbx-divider,
.abp,
.ads-container,
.abp-spacer {
    display: none !important;
}
.content {
    margin: initial;
    margin-left: 60px !important;
    max-width: initial;
    width: calc(100% - 60px);
}
#HomeContainer {
    margin-top: 0px;
    width: 100%;
    max-width: initial;
    animation-name: homethree;
    animation-duration: 5s;
    animation-timing-function: ease-out;
}
#HomeContainer .home-header {
    position: fixed !important;
    width: 100% !important;
    height: 100% !important;
    left: 0px !important;
    top: 0px !important;
}
#HomeContainer .home-header .avatar {
    position: fixed;
    left: calc(50% - 50px);
    width: 100px;
    height: 100px;
    top: 40%;
    opacity: 0;
    animation-name: homeone;
    animation-duration: 5s;
}
#HomeContainer .home-header .home-header-content {
    position: fixed;
    left: 0px;
    width: 100%;
    height: 100px;
    top: calc(40% + 50px);
    text-align: center;
    opacity: 0;
    animation-name: hometwo;
    animation-duration: 5s;
}
#mCSB_1_container ul li a span + span {
    position: absolute;
    top: 4px;
    left: 40px;
}
#mCSB_1_container ul li {
    transition: 0.1s all;
    width: 30px;
    opacity: 0.2;
    overflow: hidden;
    margin-bottom: 25px !important;
    border-bottom: solid /*[[bc1]]*/ 1px;
}
#mCSB_1_container ul li span {
    filter: brightness(0%);
}
#mCSB_1_container ul li:hover {
    opacity: 1;
    animation-name: expand;
    animation-duration: 1.5s;
    width: 120px;
    background-color: /*[[bc1]]*/;
}
#navigation #mCSB_1,
#navigation #mCSB_1_container,
#navigation .rbx-scrollbar,
#navigation #mCSB_1_container ul {
    width: 50px !important;
    height: 100% !important;
    overflow: visible !important;
}
#navigation {
    width: 50px !important;
    overflow: visible !important;
    box-shadow: none;
    outline: none;
    border: none;
}
.rbx-upgrade-now,
.rbx-nav-sponsor,
#navigation .font-bold {
    display: none;
}
.rbx-menu-item span {
    filter: invert(20%);
}
#header a {
    color: gray;
}
#header a:hover {
    color: black;
}
#header .container-fluid {
    background-color: /*[[bc2]]*/;
    border-bottom: /*[[ac1]]*/ solid 2px;
}
#header #navbar-universal-search,
#header #navbar-search-input {
    float: right !important;
    margin: 0px !important;
    height: 40px !important;
    border: none !important;
    box-shadow: none;
}
#header #navbar-universal-search #navbar-search-btn {
    height: 40px !important;
    border: none !important;
    padding-right: 10px;
}
#navigation #mCSB_1_dragger_vertical {
    display: none !important;
}
.cell-content,
.section-content, .section-header,
.container-list, .container-header {
    background-color: /*[[bc3]]*/ !important;
    box-shadow: none !important;
    border-bottom: solid 5px #dcdcdc;
}
.badge-link img,
.badge-link span:first-child {
    border-radius: 50% !important;
}
.game-card-container,
.game-card-thumb-container {
    border-radius: 0px !important;
    box-shadow: none !important;
}
#navContent {
    margin-left: 50px !important;
    margin-top: 45px !important;
}
.roblox-messages-container {
    position: absolute !important;
    overflow: auto !important;
    height: 100% !important;
    left: 0px !important;
    top: 0px !important;
    width: 100% !important;
    background-color: white !important;
}
#navContent {
    position: absolute;
    width: calc(100% - 50px);
    height: 100%;
}
}

@-moz-document domain("roblox.com") {
/* Created by Artex https://userstyles.org/styles/114128/roblox-landing */

body {
    background: transparent !important;
  }
  .content {
    background: transparent !important;
  }
  /*background*/
  #fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("http://virtualworldsforteens.com/wp-content/uploads/2015/01/Roblox-Wallpaper-5.png") !important; /*custom banner*/
	background-image: url("/*[[bgimage]]*/") !important; /*custom banner*/
    background-size: cover !important;
    background-attachment: fixed !important;
    filter: blur(5px) !important;
    background-color: rgb(240,240,240) !important;
    z-index: -1 !important;
  }

  .container-list h3, .home-header-content h1 a {
    text-shadow: 2px 2px 2px #000;
    color: #FFF !important;
  }
  
  .home-header-content a {
    color: #888888 !important;
  }
  .blog-news .news .news-link {
    background: none !important;
  }
  .blog-news .news .news-link::after {
    background: none !important;
  }
  /*navigation bar*/
  .rbx-header {
    background-color: rgba(0, 0, 0, 0.8) !important;
  }
  /*.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0px 0px 0px red inset !important;
  }*/
  .section {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
  .game .game-item {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
/*Trim content backgrounds, fill in new ones*/
  #Body, #BodyWrapper {
    background: transparent !important;
  }
  /*groups page*/
  #mid-column {
    background-color: #FFF !important;
  }
  /*develop page*/
  .BuildPageContent {
    background-color: #FFF !important;
    float: none !important;
  }
  #MyCreationsTab {
    width:792px !important;
  }
  .content-area {
    border-right: none !important;
  }
  /*devex page*/
  #DevExLeftColumn {
    height: 684px !important;
  }
  /*account settings*/
  #AccountPageRight {
    background-color: #FFF !important;
  }
  .settings-section {
    padding: 10px !important;
  }
  .divider-right {
    border: none !important;
  }
  .StandardPanelContainer .StandardPanelWhite {
    padding: 0 !important;
    padding-left: 6px !important;
  }
  
  /*games page*/
  .games-page-left {
    margin-right: 10px !important;
  }
  .games-page-lists-container {
    background: none !important;
  }
  .games-list-container {
    border-left: 10px solid rgba(255,255,255,0) !important;
    border-right: 10px solid rgba(255,255,255,0) !important;
    background-color: rgba(255,255,255,0.9) !important; 
    margin-bottom: 60px !important;
  }
  .games-list {
    min-height: 275px !important;
  }
  
  /* No Game Page ads*/
  #GamesPageRightColumnSidebar {
    display: none !important;
  }

  
  /*game page*/
  .rbx-para-overflow, .rbx-para-overflow::after {
    background: transparent !important;
  }
  /*catalog*/
  
  div#catalog {
    display: inline;
    background-color: #FFF !important;
    padding-left: 10px !important;
    padding-bottom: 5px !important;
  }
  #Body {
    background: #FFF !important;
  }
  .right-content {
    padding-left: 10px !important;
  }

  /*profile page*/
  p, p::after,
  .rbx-text-overflow, .rbx-text-overflow::after {
    background: transparent !important;
  }
  /*friends page*/
   .content .friends-subtitle, .content .friends-title {
     text-shadow: 2px 2px 2px #000;
     color: #FFF !important;
   }
  /*inventory page*/
  div.row > h1:nth-child(1), 
  .container-header .inventory-title h3,
  .recommendations-container h3 {
     text-shadow: 2px 2px 2px #000;
     color: #FFF !important;
  } 
  .pager-holder .rbx-pager {
    background-color: #FFF !important;
    border: 3px solid #FFF !important;
    border-radius: 4px !important;
  }
  /*item page*/
  #Item {
    background-color: #FFF !important;
    padding-top: 5px !important;
    padding-right: 5px !important;
  }
  #ItemContainer h1 {
    text-shadow: 2px 2px 2px #000;
    color: #FFF !important;
    padding-right: 5px;
  }
  /*forums*/
  .table-header .first {
    border-left: none !important;
  }
  .table td, .table th {
    padding-left: 10px !important;
  }
  /*place configuration*/
  .boxed-body {
    background-color: #FFF !important;
    padding-bottom: 10px !important;
  }
  #navbar {
    height: auto !important;
  }
}
