css:
/* i really want this to be global */

#stylish-edit > div#header input[type="checkbox"],
#stylish-manage > div#header input[type="checkbox"] {
  position: relative;
  top: 2px;
}

#stylish-edit > div#header {
  padding: 4px 8px 0 4px;
}

#stylish-edit > div#header input#name,
#stylish-edit button,
#stylish-manage > div#header input#search,
#stylish-manage p button,
#stylish-edit > div#header #options input,
#stylish-edit > div#header #options select,
#stylish-edit #sections input,
#stylish-edit #sections select {
  background: linear-gradient(rgba(255, 255, 255, .95), rgba(220, 220, 220, .55));
  border: 1px solid rgba(255, 255, 255, .4);
  border-radius: 3px;
  box-shadow: 1px 1px 2px rgba(0, 0, 0, .5) inset, 1px 1px 2px rgba(0, 0, 0, .5);
  color: #000;
  outline: none;
}

#stylish-manage > div#header input#search {
  margin: 8px 0;
  padding: 4px;
}

#stylish-edit > div#header input#name {
  background: #FFF;
  line-height: 20px;
  margin: 0 0 4px 0;
  padding: 2px 0px 0px 4px;
}

#stylish-edit button,
#stylish-manage p button {
  -webkit-appearance: none;
  margin: 0 4px 0 0;
  padding: 4px 4px 2px 4px;
}

#stylish-edit #sections input {
  margin-right: 4px;
}

#stylish-manage > div#header input#search:hover,
#stylish-manage > div#header input#search:focus,
#stylish-edit > div#header #options input:hover,
#stylish-edit > div#header #options input:focus,
#stylish-edit > div#header #options select:hover,
#stylish-edit #sections select:hover,
#stylish-edit #sections input:hover,
#stylish-edit #sections input:focus {
  background: #FFF;
}

#stylish-edit > div#header #enabled-label {
  vertical-align: bottom;
}

#stylish-manage > div#header > fieldset {
  margin: 0 0 20px 0;
  padding: 0 0 0 4px;
}

#stylish-manage > div#installed > div[style-id] > h2 {
  max-width: 30%;
}

#stylish-manage > div#installed > div[style-id] > p.applies-to {
  max-width: 60%;
  min-width: 1%;
}

#stylish-manage > div#header {
  background: #FEFEFE;
  box-shadow: 0 0 51px -22px #444;
  padding: 4px 8px 0 4px;
  width: 178px;
}

#stylish-edit > div#header > h1,
#stylish-edit > div#header h2,
#stylish-edit > div#header > section#options > div.aligned > label,
#stylish-manage > div#header > h1,
#stylish-manage > div#header > fieldset > legend#filters,
#stylish-manage > div#header > div#options > h2 {
  pointer-events: none;
}

#stylish-edit > div#header > section#options > div.aligned {
  display: inline-table;
  margin-top: 10px;
}

#stylish-edit > div#header > section#options,
#stylish-manage > div#header > div#options {
  border: 1px solid #999;
  border-radius: 6px;
  margin: 24px 0 16px 0;
  padding: 0 8px;
}

#stylish-manage > div#header > div#options {
  height: 154px;
}

#stylish-edit > div#header > section#options {
  height: 181px;
}

#stylish-edit > div#header > section#options > h2,
#stylish-manage > div#header > div#options > h2 {
  background: #FEFEFE;
  font-size: 12px;
  font-weight: normal;
  margin: 0 0 0 -4px;
  padding: 0 2px;
  position: relative;
  top: -8px;
  width: 42px;
}

#stylish-manage > div#header > div#options > div {
  margin: 0 0 8px 0;
}

#stylish-manage > div#header > div#options input {
  margin: 0 4px 0 0;
}

#stylish-manage > div#header > div#options > div:last-child::after {
  content: "Dark Theme: Remove /* from line 414 and */ from line 619 in code edit mode";
  float: left;
  padding-top: 10px;
}

#stylish-edit > div#header input:hover,
#stylish-edit > div#header label:hover,
#stylish-manage > div#header input:hover,
#stylish-manage > div#header label:hover {
  cursor: pointer;
}

#stylish-edit > div#header input#name,
#stylish-manage > div#header input#search {
  cursor: text;
}

#stylish-manage > div#installed {
  counter-reset: section;
  cursor: default;
  margin: 0 0 0 191px;
}

#stylish-manage > #installed::after {
  background: transparent;
}

#stylish-manage > div#installed > div[style-id]::before {
  content: counter(section) '. ';
  counter-increment: section;
  float: left;
  font-weight: bold;
  margin: 5px 4px 0 0;
}

#stylish-manage > div#installed > div[style-id] {
  border: none;
  border-top: 1px solid #DDD;
  margin: 0 0 0 1px;
  padding: 5px 4px 4px 0;
}

#stylish-manage > div#installed > div[style-id]:last-child {
  border-bottom: 1px solid #DDD;
}

#stylish-manage > div#installed > div[style-id].enabled:hover {
  background-color: rgba(0, 0, 0, .01);
}

#stylish-manage > div#installed > div[style-id].disabled {
  background-image: url(data:image/png;base64,);
  border-top: 1px solid #DDD;
  opacity: 1;
}

#stylish-edit li.applies-to-everything > button {
  margin: 0 0 0 8px;
}

#stylish-edit > div#header button,
#stylish-manage > div#header button {
  padding: 4px;
}

#stylish-manage > div#installed > div[style-id] > p button {
  float: left;
  padding: 2px;
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.disable,
#stylish-manage > div#installed > div[style-id] > p.actions > button.enable,
#stylish-manage > div#installed > div[style-id] > p.actions > button.update {
  font-weight: bold;
  padding: 3px 3px 2px 3px;
  width: 56px;
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.enable {
  color: #C00;
}

#stylish-manage > div#installed > div[style-id] a > button,
#stylish-manage > div#installed > div[style-id] > p.actions > button.disable,
#stylish-manage > div#installed > div[style-id] > p.actions > button.enable,
#stylish-manage > div#installed > div[style-id] > p.actions > button.delete, 
#stylish-manage > div#installed > div[style-id] > p.actions > button.check-update,
#stylish-manage > div#installed > div[style-id] > p.actions > button.update {
   font-size: 0;
}

#stylish-manage > div#installed > div[style-id] a > button::before {
  content: url(data:image/png;base64,); 
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.disable::before {
  content: "Disable" !important;
  font-size: 13px !important;
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.enable::before {
  content: "Enable" !important;
  font-size: 13px !important;
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.delete::before {
  content: url(data:image/png;base64,); 
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.check-update::before {
  content: url(data:image/png;base64,);
}

#stylish-manage > div#installed > div[style-id] > p.actions > button.update::before {
  content: "Update";
  font-size: 13px;
}

#stylish-manage > div#installed > div[style-id] > h2,
#stylish-manage > div#installed > div[style-id] > p.actions {
  display: inline;
}

#stylish-edit #url > img,
#stylish-manage > div#installed > div[style-id] > h2 > a {
  background-image:  url(data:image/png;base64,);
  opacity: .75;
}

#stylish-edit #url > img {
  height: 22px;
  margin: -2px 0 0 2px;
  padding-left: 22px;
  width: 0;
}

#stylish-manage > div#installed > div[style-id] > h2 > a:hover,
#stylish-edit #url > img:hover {
  opacity: 1;
}

#stylish-manage > div#installed > div[style-id] > h2 > a {
  background-color: transparent;
  background-repeat: no-repeat;
  background-position: center;
  float: left;
  height: 22px;
  margin: -2px 4px 0 0;
  text-decoration: none;
  width: 22px;
}

#stylish-manage > div#installed > div[style-id] > h2 > a:hover {
  opacity: 1;
}

#stylish-manage > div#installed > div[style-id] > h2 {
  margin: 0 4px;
  vertical-align: top;
}

#stylish-manage > div#installed > div[style-id] > h2 img {
  display: none;
}

#stylish-edit button:hover,
#stylish-manage p button:hover {
  background: #FFF;
  cursor: pointer;
}

#stylish-manage > div#installed > div[style-id] > h2 {
  -webkit-appearance: textfield;
  margin-left: 0;
  overflow: hidden;
  padding: 2px 0 0 0;
  text-overflow: ellipsis;
  white-space: nowrap;
}

#stylish-manage > div#installed > div[style-id].disabled > h2,
#stylish-manage > div#installed > div[style-id].disabled > p.applies-to {
  color: #C00;
}

#stylish-manage > div#installed > div[style-id] > p.applies-to {
  -webkit-appearance: textfield;
  display: inline-block;
  margin: -3px 0 0 0;
  min-width: 1%;
  padding: 0;
  word-wrap: break-word;
}

#stylish-manage > div#installed > div[style-id]::before {
  text-align: right;
  width: 28px;
}

#stylish-manage > div#installed > div[style-id].disabled > h2::after {
  content: ""; 
}

#stylish-manage > div#installed > div[style-id] p > span.update-note {
  position: relative;
  top: 0;
  vertical-align: top;
}

#stylish-edit .CodeMirror {
  max-height: 874px;
}

#stylish-edit > section#sections >  h2 {
    margin: 0.5rem 0 0 0;
}

#stylish-edit > section#sections > div {
  margin: 0 8px 0 0;
  padding: 0;
}

#stylish-edit > section#sections > div:not(:first-of-type) {
  border-top: 1px dashed #999;
  margin-top: 8px;
  padding-top: 8px;
}

#stylish-edit > section#sections div.applies-to {
  line-height: 22px;
}

#stylish-edit > section#sections div.applies-to img {
  vertical-align: middle;
}

#stylish-edit > section#sections ul {
  margin-top: 3px;
}

#stylish-edit > section#sections >  h2 > span#sections-heading,
#stylish-edit > section#sections li.applies-to-everything {
  cursor: default;
}

#stylish-manage p#manage-text {
  counter-reset: section;
  cursor: default;
  margin: 0;
}

#stylish-manage p#manage-text > a {
  line-height: 20px;
  text-decoration: none;
}

#stylish-manage p#manage-text > a::before {
  content: "â€¢";
  clear: both;
  float: left;
  margin-right: 4px;
  opacity: 1;
}

#stylish-manage p#manage-text > a:nth-child(3) {
  clear: both;
  float: left;
}

#stylish-manage p#manage-text > a:nth-child(3)::after {
  content: "|";
  margin-left: 3px;
}

