css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.jjwxc.net") {
  /*渐变属性*/
  *{
    transition-timing-function: ease;
    transition-duration: .15s;
    transition-property: border-color, background-color, color;
  }  
  body{
    background-color: #223 !important;
    color: #aaa;
  }
  a:link, a:visited{
    color: #baa;
    text-decoration: none;
  }
  a:hover{
    color: #aaa;
  }
  a[href]:hover{
    color: #b99 !important;
    text-decoration: none;
  }
  span[title="展开所有"]{
    color: #a88 !important;
  }
  td[bgcolor="#E8F3FF"],
  table[bgcolor="#E8F3FF"]{
    background: 0 0 !important;
  }
  a#adminlink font{
    color: #223 !important;
    text-shadow: 1px 1px 1px #000;
  }
  li.line{
    border: 0;
  }
  /*清理元素*/
  td.textbook,#dingbuFix,.image1,.image2,.image3, .image4, .image5, .image6, .image7, .image8, .image9, .image10, .image11, .image12, .image17, #footer,#bottom, #logo, #charlogo, #selecthead, #tongLanDown, #htAboveFix, img[alt="返回上层"], img[alt="上一页"], img[alt="下一页"]{
    display: none !important;
  }
  /*字色处理*/
  font[color="#000000"]{
    color: #fff;
  }
  font[color="#FF0000"],font[color="red"]{
    color: #c00;
  }
  hr{
    color: #223;
    border-color: #223;
  }
  /*回复框*/
  #postform{
    background: #28283a;
    box-shadow: 1px 1px 3px #111;
    border-radius: 4px;
    padding: 10px;
  }
  #postform table{
    border: 0;
    border-spacing: 0 !important;
    width: auto;
  }
  #postform td{
    border: 0;
  }
  #postform td[width="18%"]{
    width: 8%;
  }
  /*输入界面*/
  textarea, input[type="text"], select{
    background: #111;
    border: 0;
    box-shadow: inset -1px -1px 4px #000;
    color: #aaa;
    padding: 2px 5px;
  }
  /*按钮样式*/
  input[type="button"], button{
    background: #2f2f46;
    border:0;
    border-radius: 3px;
    box-shadow: 1px 1px 3px #111;
    cursor:pointer;
    color: #aaa;
    font-size: 12px;
    padding: 2px 5px;
  }
  input[type="button"]:hover, button:hover{
    background: #335;
    box-shadow: .5px .5px 1px #000;
  }
  input[type="submit"]{
    background: #690;
    border:0;
    border-radius: 2px;
    box-shadow: 1px 1px 3px #111;
    cursor:pointer;
    font-size: 12px;
    padding: 2px 5px;
  }
  input[type="submit"]:hover{
    background: #7a0;
    box-shadow: .5px .5px 1px #000;
  }
  .keleyi{
    background: #2f2f46;
    border:0;
    border-radius: 3px;
    box-shadow: 1px 1px 3px #111;
    color: #aaa;
    padding: 2px 5px;
  }
  .keleyi:hover{
    background: #335;
    box-shadow: .5px .5px 1px #000;
  }
  /*整理成卡片布局*/
  #msgsubject{
    color: #fdd;
    font-size: 18px;
    padding: 0 15px 10px;
  }
  tr[class^="reply_"]:nth-child(4n),
  tr[class^="reply_"]:nth-child(4n+1),
  tr[class^="comment_"]:nth-child(2),
  tr[class^="comment_"]:nth-child(5),
  #pager_top+table+div, #boardname+table+div{
    display: none !important;
  }
  tr[class^="reply_"]:nth-child(4n+2){
    background: #28283a;
    box-shadow: 1px 1px 3px #111;
    border-radius: 4px;
  }
  #pager_top+table,#boardname+table/*主帖*/{
    background: #28283a;
    box-shadow: 1px 1px 3px #111;
    border-radius: 4px;
    margin: 10px auto 20px;
    padding: 10px 0 4px;
  }
  tr[class^="reply_"]:nth-child(4n+3){
    color: #555;
    line-height: 5px;
    position:relative;
    top: -25px;
  }
  td.authorname{
    padding: 5px 15px;
  }
  /*收藏按钮*/
  #delfavorite,#addfavorite{
    color: #a11 !important;
    padding-right: 15px;
    text-shadow: .5px .5px 2px #000;
  }
  #addfavorite:before{
    content: "☆";
    color: #b91 !important;
    font-size: 20px;
  }
  #delfavorite:before{
    content: "★";
    color: #b91 !important;
    font-size: 20px;
  }
  /*页码样式*/
  a[href^="showmsg.php"][href*="&page="]{
    color: #690;
  }
  /*内容排版*/
  #topic{
    padding: 5px 15px 30px;
    line-height: 20px;
  }
  br{
    margin-bottom: 12px;
  }
  /*弹出框样式*/
  .blockMsg{
    background: #223 !important;
    color: #aaa !important;
  }
  .blockMsg td[onclick="$.unblockUI()"]{
    color: #c00 !important;
  }
  .blockMsg .boardname{
    font-size: 12px;
    background: #223;
  }
  .blockMsg .check{
    background: #28283a;
  }
  /*图片不越界*/
  img{
    max-width: 100%;
  }
  
  /*主页卡片布局*/
  .cont01,.cont02,.cont03,.cont04,.cont05{
    background: #28283a;
    box-shadow: 1px 1px 3px #111;
    border: 0;
    border-radius: 4px;
    margin: 0 0 8px;
  }
  .cont04,.cont05{
    height: 275px;
  }
  .cont02 .headline,.cont03 .headline, .cont04 .headline,.cont05 .headline{
    background: 0 0;
    color: #ccc;
    padding: 15px 0 3px 20px;
  }
  .cont02 li, .cont03 li, .cont04 li, .cont05 li{
    border: 0;
    list-style-image: none;
  }
  .headline + ul{
    margin: 0 18px;
  }
  .cont01 > .f1:first-child{
    opacity: 0;
  }
  /*首页头部*/
  #top02{
    height: auto; width: auto;
  }
  #adnav{
    border: 0;
    height: auto;
    width: auto;
    padding: 0;
    margin: 0;
  }
  #navlist{
    background: 0 0;
    height: auto;
    width: auto;
    margin: 0;
    padding: 0 0 15px;
  }
  #navlist li{
    margin: 0 10px;
  }
  #navlist li a{
    color: #a88;
  }
  /*去页头*/
  tr[bgcolor="#F9CCDE"], tr[bgcolor="#666666"]{
    display: none;
  }
  td[bgcolor="#FCE0EB"][colspan="5"]{
    background: #223;
    color: transparent;
    padding: 5px;
  }
}


@-moz-document regexp("http://bbs\.jjwxc\.net/(bindex|board).*"){
  /*区页*/
  table{
    border-spacing:0 !important;
  }
  td[bgcolor="#FFCC99"]{
    background: #28283a;
    border: 0;
  }
  tr[bgcolor="#FFF5EC"]{
    background: #223;
    line-height: 30px;
  }
  tr[bgcolor="#FFE7F7"]{
    background: #28283a;
  }
  tr[bgcolor="#FFE7F7"]:hover{
    background: #2f2f46;
  }
  /*调整列表间距*/tr[bgcolor="#FFE7F7"] > td{
    height: auto;
    vertical-align: top;
    padding: 4px 5px 4px 0;
  }
  tr[bgcolor="#FFE7F7"] > td:nth-child(5){
    color: #690;
  }
  tr[bgcolor="#E8F3FF"]{
    background: #2f2f46;
  }
  td[bgcolor="#FFFFFF"]{
    background: #2f2f46;
  }
  #boardname{
    border: 0 !important;
  }
  #boardname > div:first-child{
    font-size: 18px;
    font-weight: 600;
    padding: 0 !important;
  }
  #boardname > div:last-child{
    background: #111;
    box-shadow: inset -1px -1px 4px #000;
    border-radius: 6px;
    overflow: hidden;
  }
  #msglist{
    background: #28283a;
    box-shadow: 1px 1px 3px #111;
  }
  /*换页按钮*/
  div[align="right"]{
    padding: 15px 0 0;
  }
  div[align="right"] a[href^="board.php"][href*="&page="],
  div[align="right"] a[href^="board.php"][href*="&page="]+a{
    background: #28283a;
    padding: 4px 6px;
    border:0;
    border-radius: 3px;
    box-shadow: 1px 1px 3px #111;
  }
  div[align="right"] a[href^="board.php"][href*="&page="]{
    color: #ccc;
    font-weight: 600; 
  }
  div[align="right"] a[href^="board.php"][href*="&page="]:hover{
    color: #fff !important;
    background: #335;
    box-shadow: .5px .5px 1px #000;
  }
  span[style="color:#0000FF;"],span[style^="color:blue"]{
    color:#690 !important;
  }
  #subidform{
    border: 0 !important;
    padding: 8px;
  }
  .maincenter{
    margin: 3px 5px;
  }
}


@-moz-document regexp("http://bbs\.jjwxc\.net/(search).*"){
  /*搜索页*/
  hr[noshade="noshade"]{
    display: none;
  }
  hr[noshade="noshade"][width="200"]+table{
    border: 0;
    margin: 10px auto 5px;
    width: 700px;
  }
  hr[noshade="noshade"]+table td{
    border: 0;
  }
  table[bordercolorlight="#808000"][bordercolordark="#FFF9E1"]{
    border: 0;
  }
  table[bordercolorlight="#808000"][bordercolordark="#FFF9E1"] td{
    border: 0;
    padding-top: 4px;
    padding-bottom: 4px;
  }
  table[bordercolorlight="#808000"][bordercolordark="#FFF9E1"] > tbody > tr:nth-child(2n+1){
    background: #28283a;
  }
  input[name="keyword"]{
    width: 90%;
  }
}
