css:
@-moz-document domain("eddb.io") {
body.site-eddb, body.site-ross {
    background-color: #222;
    color: lightgray;
}

.page-header h1 {
    color: #2A88AA !important;
}

h1 {
    border-bottom-color: #666;
    color: #2A88AA;
}

.filter-group h3 {
    color: lightgray;
}

.panel {
    background-color: #111;
    color: gray;
}

.box .box-body {
    background-color: #111;
    color: lightgray;
}

.footer {
 	background-color: #111;
    color: gray;
}

div.FixedHeader_Cloned th {
    background-color: #444 !important;
    color: #DDD;
}

.filter-group .filter-label {
    background-color: #111;
    color: lightgray;
}

.panel {
    color: #DDD;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #111;
}

.table-striped > tbody > tr:nth-of-type(even) {
    background-color: #444;
}

a {
	color: #2A88AA;
}

a:focus {
    color: #9AF;
}

a:hover {
    color: #ABF;
}

.select2-choice, .select2-drop, .select2-search>input{
    background-color: #444 !important;
    color: #CDCDCD !important;
}

.select2-search>input {
    background-color: #444 !important;
    background-image: none;
    color: #DDD !important;
}

.modal-content {
 	background-color: #444;
    color: #DDD;
}

.minmax-legend {
 	color: #BBB;   
}

.panel table.panel-table .commodity-buy, .FixedHeader_Header table.panel-table .commodity-buy {
    color: #59F;
}

.worse {
 	color: lightsalmon;   
}

.better {
	color: lightgreen;
}

.select2-container .select2-choices, .select2-search-field {
	background-color: #444;
}

.select2-search-field {
	background-color: #444;
}

.select2-search input.select2-active {
    background-color: #444;
    background-image: url(http://eddb.io/assets/b752da7a/select2-spinner.gif), linear-gradient(#444 85%, #555 99%);
}

.select2-container-multi .select2-choices .select2-search-choice {
	background-color: #333;
    color: #CCC;
}

.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-selection-limit {
    background-color: #333;
    color: #CCC;
}

.form-control {
    background-color: #444;
    color: #CCC;
}

table.dataTable tbody tr{
    background-color: #333;
}

.panel table.panel-table .route-result-item:nth-child(even), .FixedHeader_Header table.panel-table .route-result-item:nth-child(even) {
    background-color: #444;
}

code {
    background-color: #555;
    color: #FFF;
}

.loops-result .loop-details .loop-actions .loop-action-meta {
    color: #49A;
}


.loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit{
    background-color: orange;
    color: black;
    
}

.loops-result .loop-details .loop-result-wrapper .loop-result-profit small {
    color: #235;
}

.facilities .facility.no {
    color: salmon
}

.facilities .facility.yes {
    color: lightgreen;
}

.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    background-color: #333;
    color: #CCC;
}

.pagination > li > a, .pagination > li > span {
    background-color: #333;
    color: #CEF;
}

.not-set {
    color: #F77;
}

code.changeLog-diff {
    background-color: #444;
}

code.changeLog-diff .changeLog-diff-name {
    color: #AAA;
}

.popover {
 	background-color: #000;   
}

.popover.left > .arrow:after {
    border-left-color: #000;
}

.loop-list .loop-details .loop-result-wrapper .loop-result-profit, .loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit {
    background-color: orange;
}

.form-control[disabled], form-control[readonly], fieldset[disabled] .form-control {
    background-color: #AAA;
    color: #000;
}

.site-ross .system-presences .system-presence-row.isControlling {
    background-color: #355;
}

.material-outer {
    color: #333;
}

.icon-planet:before {
    color: lightblue;
}

table.table-eddnlog .action-text i {
    color: white;
}

.stationSearchResult table thead tr th, .systemSearchResult table thead tr th, .factionSearchResult table thead tr th, .bodySearchResult table thead tr th {
    border-right-color: #0c8cb5;
}

.stationSearchResult table tbody tr:first-child td, .systemSearchResult table tbody tr:first-child td, .factionSearchResult table tbody tr:first-child td, .bodySearchResult table tbody tr:first-child td {
    border-top-color: #0c8cb5;
}
}
