  background-repeat: repeat !important;;
  position: fixed !important;;
  }


html body div.container div#wrapper div#right, div#ads-bottom-bar-container a#ads-bottom-bar-ad, div#ads-bottom-bar-container a#ads-bottom-bar-close{
    display: none !important;
