    display: none;
    height: 0px !important;
  }
  
  .container-col-outer .container-col-left {
    width: 100% !important;
  }
  .container-col-inner {
    width: 100% !important;
  }
  .delfi-article-wrapper {
    margin-right: auto;
    margin-left: auto;
    width: 80% !important;
}
  .related-box{
    display:none !important;
    
  }
  .delfi-article-banner{
    height: 0px !important;
  }
  .container-col-inner.container-col-separator,.container-col-outer.container-col-separator {
    background: none !important;
}
}

@-moz-document domain("www.15min.lt") {
html body.cosite-default div.top-banner.row-24.clearfix {
    display: none;
}
  html body.cosite-default div.wrapper div.row-24.block-main div.block-right.col-6.no-padding{
    display:none;
  }
  html body.cosite-default div.wrapper div.row-24.clearfix div.block-right.content-right.col-6.no-padding{
