css:
@-moz-document domain("www.twitch.tv") {
.warp {
    background-color: #17141f!important;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    min-height: 100vh;
}

.leaf {
    color: #a49fad;
    background-color: #201c2b;
    box-shadow: #2c2541 0 0 0 1px inset;
    font-size: 1.4rem;
    line-height: 3rem;
    height: 3rem;
    border: 0;
    padding: 0 1rem;
    padding-left: 4rem;
    outline: 0;
    width: 100%;
    transition: .3s ease;
}

.tse-scroll-content {
    width: auto!important;
    height: auto!important;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
}

.warp__item--toggled>a {
    box-shadow: 3px 0 0 #6441a4 inset!important;
    background: #201c2b!important;
    color: #faf9fa!important;
}

.warp__drawer {
    background: #201c2b!important;
    box-shadow: 0 -1px 0 #201c2b!important;
    padding-bottom: 1rem!important;
    position: absolute;
    bottom: 100%;
    overflow: hidden;
    max-height: 100vh;
    transform: translate3d(0,0,0);
    transition: all .25s;
    width: 100%!important;
}

.warp__item--anchor {
    background: #201c2b!important;
    padding: 1rem 0 1.5rem;
    position: relative;
    z-index: 999999!important;
}

.warp__item a:hover {
    color: #faf9fa!important;
    background: #201c2b!important;
    text-decoration: none;
}

.leaf {
    color: #a49fad!important;
    background-color: #201c2b!important;
    box-shadow: #2c2541 0 0 0 1px inset!important;
    font-size: 1.4rem;
    line-height: 3rem;
    height: 3rem;
    border: 0;
    padding: 0 1rem;
    padding-left: 4rem;
    outline: 0;
    width: 100%;
    transition: .3s ease;
}

.leaf--small {
    font-size: 1.2rem;
}

.leaf:focus {
    box-shadow: #4b367c 0 0 0 1px inset!important;
}

.warp__tab:after {
    content: '';
    position: absolute;
    bottom: -.5rem;
    left: 0;
    width: 100%;
    opacity: 0;
    background-color: #7d5bbe!important;
    height: 1px;
    transition: all .25s;
}

#search-panel {
    background: #201c2b!important;
    border: none;
}

#search-panel .titleBar, #search-panel .titleBar .titleBar__title, #search-panel .resultView__titlesep {
    background: #201c2b!important;
    color: #d3d3d3;
}
