    border-color: #7d5bbe!important;
}

#search-panel .resultView .card .card__layout.isActive:before {
    background: #6441a4;
}

#large_nav .content .nav_section a:not(.button):hover {
    text-decoration: none;
    background-color: #201c2b!important;
    color: #efeef1;
}

#large_nav .game_filter.selected a:not(.button) {
