    color: white!important;
    border-left: 4px solid #6441a4;
}

#large_nav .content .nav_section .left-col-dropdown:hover {
    cursor: pointer;
    background-color: #201c2b!important;
    background-position: center -75px;
}

#left_col .warp input {
