css:
@-moz-document domain("generals.io") {
.maroon { background-color: #822e2e !important; }
.green {background-color: #008937 !important; }
.red { background-color: #d21524 !important; }
.purple {	background-color: #731f8a !important; }
.darkgreen { background-color: #195034 !important; }
.blue { background-color: #1337da !important; }
.orange { background-color: #f5a514 !important; }

.city { background-color: #929292; }
.mountain { background-color: #cac8c8 !important; }

#map td {
	width: 48px;
	height: 48px;
  max-width: 48px;
  max-height: 48px;
  background-size:	36px 36px;
}

#game-leaderboard td {
	padding: 4px;
}

#turn-counter {
	padding: 8px 24px; 
	font-weight: bold;
}

#clear-moves-button {
	padding: 8px 16px; 
  font-size: 13px;
}

#replay-top-left {
	top: 40px; 
}

.chat-messages-container {
  min-width: 360px;
 	max-height: 140px;
}

.server-chat-message {
	font-family: Quicksand; 
}

#in-game-chat input {
	width: 360px; 
	padding: 6px;
}

button,
button.small,
button.big { 
	padding: 5px 20px;
  font-size:	20px;
}

#main-menu-reddit-button {
	top: 16px;
  left: 16px;
}

#leaderboard-button {
	top: 16px;
  right: 16px;
}

#replaylist-button {
	top: 85px;
  right: 16px;
}

#leaderboard-button img, 
#replaylist-button img {
	width: 32px;
  height: 32px;
}

[href="mailto:generalsiogame@gmail.com"] {
	left: 16px !important;
  bottom: 16px !important;
}

[href="/versions"] {
	right: 16px !important;
  bottom: 16px !important;
}

.main-title {
	font-size: 60px; 
}

}
