@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("seasonvar.ru") {
  
  .mgbox{ display: none; } 

/* body */
  
  .avp_zid_456{
    display: none !important;
  }
  
  body{
    background-color: #06232c !important;
  }
  
/* левый бар с ебучей рекламой */  
  
  .main-table tbody tr td.td-for-left-block{
    display: none;
  }

/* убрал ебучую левую полоску, поставив фон как у body */  
  
  .main-center-block{
    background: url(http://cdn.seasonvar.ru/images/body.jpg);
  }

/* body реклама */  
  
  #reklama1{
    display: none;
  }
  
  #adv-block{
    display: none;
  }
  
  #b_sl_51{ /* скрипт реклама */
    display: none !important;
  }
  
/* реклама под плеером */
  
  #b7574d8c9c{
    display: none !important;
  }

/* озвучка */
  
  #translateDiv{
    margin-bottom: 40px !important;
   
  }

/* таблицы */
  
  .svtab{
    margin-top: 20px;
    background: #E7E7E7 !important;
  }
  
  .svtabr{
    border: 1px solid #151515 !important;
  }
  
  .svtab_wrap_li span{
    border: none !important;
    color: #000 !important;
  }
  
  .svtab_wrap_li span.act, .svtab_wrap_li span[data-tab]:hover{
    margin-top: 0;
    padding-top: 0;
    background-color: #E7E7E7 !important;
    color: #C01462 !important;
  }
 
/* лишний бордер в комментариях */
  
  #svc_frame{
    border: 0 !important;
  }
  
/* минус разделители в списке сезонов*/
  
  .svtabr .seasonlist a{
    border-bottom: 0 !important;
  }
    
/* реклама под плеером */
  
  #bn_U4TYch5x6q{ 
    display: none !important;
  }
  
  .pgs-player div:last-child div:first-child{
    height: 0 !important;
    display: none !important;
  }

}
