css:
@-moz-document url-prefix("http://www.eldarya.fr/"), url-prefix("http://fa1.eldarya.com/"), url-prefix("http://www.eldarya.ru/"), url-prefix("http://www.eldarya.com/"), url-prefix("http://www.eldarya.com.br/"), url-prefix("http://www.eldarya.de/"), url-prefix("http://www.eldarya.es/"), url-prefix("http://www.eldarya.fi/"), url-prefix("http://www.eldarya.hu/"), url-prefix("http://www.eldarya.it/"), url-prefix("http://www.eldarya.pl/") {
body {
background: url("https://68.media.tumblr.com/c7d2e79f644ea0c2acc674543f77bbcb/tumblr_oj2qv1B2fm1u0r10co3_r2_1280.png") fixed top center;;
    background-size: cover;
    overflow-y: scroll;
}
#header #header-top {
    background-color: rgba(66, 118, 159, 0.5);
    border-radius: 10px;
}
#footer {
    position: absolute;
    height: 42px;
    width: 100%;
    bottom: 0;
    left: 0;
    background-color: rgba(66, 118, 159, 0.5);
}

#header-portraits {
    width: 197px;
    height: 132px;
    background:transparent;
    
}

#player-infos {
    background:transparent;
    height: 256px;
    padding: 0 10px 0 20px;
    text-align: center;
}


#player-infos:after {
  background:transparent;
     content: '';
    position: absolute;
    left: 100%;
    top: 0;
    width: 36px;
    height: 125px;
    z-index: 1; 
  }

  #container .page-main-container {
    background-color: rgba(192, 191, 164, 0.25);;
    border-radius: 8px;
    padding: 20px;
    min-height: 400px;
    position: relative;
    box-shadow: inset 1px 1px 3px 1px rgba(47, 62, 113, 0);
                    }
    #container .page-main-container:after {
      content: '';
      border-radius: 8px;
      background-position: 0 0, 100% 0, 0 0, 0 100%;
    
      background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
      box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0);
    }
#main-menu ul {
    text-align: center;
    white-space: nowrap;
    background-color: rgba(255, 228, 225, 0);
    box-shadow: inset 1px 1px 3px 1px  rgba(255, 228, 225, 0);
}

#main-menu ul:after {
      background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
      box-shadow: 1px 2px 3px 0 rgba(255, 228, 225, 0);
}

    #index-news .date {
    background-color: #c0bfa4;
}
#index-news {
    background-color: rgb(52, 56, 111);
    border-color: rgba(107, 117, 255, 0.63);
}#news-list .news + .news {
    border-top: 2px solid rgba(79, 63, 140, 0.42);
}
#news-list {
    background: linear-gradient(0deg, rgb(145, 191, 239) 0%, rgb(224, 232, 255) 100%);
}
.index-carousel {
    position: relative;
    height: 231px;
    width: 522px;
    border-radius: 10px;
    border: 2px solid rgb(27, 28, 64);
    background-color: rgba(168, 207, 239, 0.77);
    overflow: hidden;
}
.index-main-container .index-carousel-description {
    color: rgb(0, 0, 0);
}
#player-headerPortrait {
    width: 112px;
    height: 113px;
    top: 13px;
    right: 12px;
}
#player-infos .player-name {
    color: #fff;
    font-weight: 600;
    font-family: "Banda Regular", sans-serif;
    position: relative;
    height: 32px;
    line-height: 32px;
    display: inline-block;
    font-size: 16px;
    margin: 25px 0;
    border: solid 2px #CFE0FF;
    background-color: #343B9C;
}
#index-player {
    position: absolute;
    margin: 0 auto;
    width: 420px;
    height: 600px;
    right: 20px;
    top: 20px;
    border: solid 2px rgba(241, 207, 221, 0.45);
    border-radius: 10px;
    overflow: hidden;
    background-color: #f1cfdd;
}
.episode-container .text {
    z-index: 100;
    text-align: justify;
    position: absolute;
    font-family: Suplexmentary;
    line-height: 1em;
    font-size: 20px;
    overflow: hidden;
    background: linear-gradient(rgba(126, 115, 181, 0.85098) 0%, rgba(34, 29, 109, 0.85098) 89%, rgba(50, 25, 82, 0.298039) 100%);
}
#right-profile-tab {
    float: left;
    width: 369px;
    background-color: rgba(192, 191, 164, 0.72);
    position: relative;
    min-height: 111px;
    padding: 15px 15px 30px;
    margin: 10px 0px 0px 10px;
    border-radius: 10px;
    overflow: auto;
}
#library-container li {
    background-color: rgba(66, 118, 159, 0.76);
}
.mall-category, .mall-product, .mall-variation {
    width: 106px;
    height: 124px;
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 5px;
    background: rgba(225, 236, 253, 0.91);
    margin: 5px;
    padding: 7px;
    border-radius: 3px;
}
#mall-productDetail-info {
    width: 626px;
    clear: both;
    height: 85px;
    position: relative;
    box-shadow: black 0px 0px 5px;
    text-align: center;
    background: rgba(225, 236, 253, 0.68);
    margin: 7px 5px;
    padding: 7px;
    border-radius: 3px;
}
.guard-gem.guard-4 {
    background-image: url("http://i64.tinypic.com/2vuaez5.jpg");
}

.guard-gem.guard-2 {
    background-image: url("http://i65.tinypic.com/2yx3ups.jpg");
}


.guard-gem.guard-3 {
    background-image: url("http://i68.tinypic.com/hw9xxv.jpg");
}


.guard-gem.guard-1 {
    background-image: url("http://i65.tinypic.com/24b19jd.jpg");
}
#index-ranking {
    display: inline-block;
    height: 344px;
    width: 280px;
    background-color: rgba(48, 52, 148, 0.85);
    border-radius: 10px;
    border: 2px solid #aeb4ff;
}
#ranking-list .ranking-first {
    height: 101px;
    color: rgb(255, 233, 206);
}
#ranking-list {
    background: linear-gradient(0deg, rgba(69, 67, 150, 0.02) 0%, #667dbd 76%, rgba(139, 143, 202, 0.52) 96%);
}
.marketplace-itemsForSale-item {
    height: 105px;
    float: left;
    cursor: pointer;
    position: relative;
    box-shadow: black 0px 0px 5px;
    background: #E4E4F3;
    margin: 7px;
    padding: 7px;
    border-radius: 3px;
}
img.abstract-icon {
    background-color: #E4E4F3;
}
.inventory-item {
    width: 80px;
    height: 80px;
    background: #EBEBFF;
    margin: 5px;
    padding: 2px;
    float: left;
    position: relative;
    border-radius: 3px;
    box-shadow: 0px 0px 5px black;
}
.appearance-category, .appearance-item {
    background: rgba(229, 229, 255, 0.5);
    margin: 5px;
    float: left;
    cursor: pointer;
    position: relative;
    border-radius: 3px;
    box-shadow: 0px 0px 5px black;
}
#selectedCategory {
    border: 1px solid rgba(0, 0, 255, 0.61);
    border-bottom-width: 2px;
    background: #EAEAFF;
    position: absolute;
    border-radius: 4px;
    cursor: pointer;
    width: 100%;
}
body.night {
    background: #4da8fd
url("https://68.media.tumblr.com/c7d2e79f644ea0c2acc674543f77bbcb/tumblr_oj2qv1B2fm1u0r10co3_r2_1280.png") fixed top center;
    background-size: cover;
}
}
