@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("otvet.mail.ru") {

/* реклама */  
  
  .column_right, .adv-slot-5647, .adv-slot-17052-link, .tb-25154557{
    display: none !important;
  }
  
/* всякая ненужная хуета*/
  
  .OTVET-3541-footer-slots, .screen_medium, .mailru-visibility-check, .OTVET-3541-footer-slots-active, .cmrinf-ladyinf__contentbox, .h-title--w, .h-title, .yap-answ-del-horizontal{
    display: none !important;
  }
  
  #ColumnCenter{
    min-height: 0;
  }

/* квадратные иконки */  
  
  .avatar, .small-avatar, .form--ava, .u--head--ava, .avatar__img, .pm-toolbar__button__icon__img,  .pm-toolbar__button__icon__img_notext, .nf--img, .pm-toolbar__button__icon__img_avatar, .pm-toolbar__button__avatar, .t-ava--ava {
    
    border-radius: 0 !important;
  }
  
  .user-small-button, .user-small__ava, .user__ava img{
    border-radius: 0 !important;
  }
  
  .base .user__ava{
    border-radius: 0 !important;
  }
  
  .top-others .user__ava{
    border-radius: 0 !important;
  }
  
  .x-ph__auth_list_active .x-ph__auth_list__item__avatar{ /* иконка в выпадающей менюшке при нажатии на почту в верхнем правом углу */
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
  }
  
  .x-ph__auth_list_active .x-ph__auth_list__item__avatar__camera::after{ /* маленькая подкнопочка в предыдущей кнопке */
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
  }
  
  
/* цвет шапки */
  
  .w-pm-menu, .pm-menu, .pm-toolbar__search__container{
    background-color: #507CA9;
  }

 
/* менюшка */  
  
  .s-link:hover, .pm-toolbar__button__inner:hover{
    background: none;
  }
  
  .s-link, .pm-toolbar__button__inner{
    background: none;
  } 
  
  
/* hide separator & картинка маил хуйру 241x52 */  
  
  .pm-toolbar__separator{
    display: none;
  }
  
  .pm-logo__link img{
    display: none;
  }

  .pm-logo a {
    background-image: url(https://3.downloader.disk.yandex.com/disk/9303c97208ce8a7fd7a62c41c770d69a6ed7d5c21c312094fe20481ee13804cc/586a0ea1/XcOPHVsisIpcKqkOZyBBLLqhsqILvJHLqgpGr7WvaKzNj0LY8Yoo8ScyhumsE3427BlfILtWLuYjzWtEP5mKMQ%3D%3D?uid=0&filename=logo.png&disposition=inline&hash=&limit=0&content_type=image%2Fpng&fsize=4604&hid=513d798a895de0ab606a4266f3b43a85&media_type=image&tknv=v2&etag=004235175b3432d14c98c7039429bea9) !important;
  }
  
  
/* категории */  
  
  .pm-toolbar__button__inner_open{
    background: #FFF !important;
    color: #000 !important;  
  
  }
  
/* кнопка "изменить аватар" */  
  
  .btn-blue{
    background: #507CA9;
  }
  
  .u--head--change-avatar{
    border-radius: 0;
    width: 134px;
  }
  
  .icon__pic{ /* кнопочка рядом с текстом "изменить аватар" в кнопке */
    display: none;
  }


}
