css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fivem.net"),domain("discourse.org") {
.docked .d-header,.list-controls .category-dropdown-menu,.list-controls .category-breadcrumb li.bullet > .badge-category,.d-header,.docked .d-header {
    background-color: #222222 !important;
}
body,.drop-down-visible .d-header .icons .active .icon,.d-header .icons .icon:hover {
    background-color: #333 !important;
}
.user-menu .notifications .read,html{
    background-color: #333;
}
.topic-list a.title,.badge-wrapper.bullet span.badge-category,.category-list tbody .category h3 a[href],.list-controls .category-dropdown-menu .home,.user-main .nav-stacked li > a.active,.list-controls .category-breadcrumb li.bullet > .badge-category {
    color: #FFF  !important;
}
.drop-down-visible .d-header .icons .active .icon {
    border-top: 1px solid #181818 !important;
    border-left: 1px solid #181818 !important;
    border-right: 1px solid #181818 !important;
}
body input[type="text"]:focus, body input[type="password"]:focus, body input[type="datetime"]:focus, body input[type="datetime-local"]:focus, body input[type="date"]:focus, body input[type="month"]:focus, body input[type="time"]:focus, body input[type="week"]:focus, body input[type="number"]:focus, body input[type="email"]:focus, body input[type="url"]:focus, body input[type="search"]:focus, body input[type="tel"]:focus, body input[type="color"]:focus {
    border-color: #FFF;
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.3),0 0 8px #FFF;
}
.menu-panel {
    border: 1px solid #181818;
    box-shadow: 0 2px 2px rgba(0,0,0,0.25);
    background-color: #333;
}
body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
    background-color: #222;
    color: #FFF;
}
.user-menu .notifications li:hover,div.menu-links-header a:hover,.menu-panel ul.menu-links li a:hover, .menu-panel ul li.heading a:hover,.hljs,blockquote,aside.quote .title,.timeline-container .topic-timeline .timeline-handle,.badge-card .badge-contents .badge-icon {
    background-color: #222;
}
.user-menu .notifications li span,.d-editor-input,html,#topic-title h1 a,.hljs,.topic-map .avatars, .topic-map .links, .topic-map .information,#user-card,#user-card .metadata h3 .desc, #user-card .metadata h3 a,#user-card h1 a,.stats-section li.linked-stat a,.user-main .user-stream .excerpt,.user-main .about .secondary dd,#reply-control.edit-title .contents input#reply-title {
    color: #FFF;
}
.user-main .about .details {
    background: rgba(255, 255, 255, 0.21);
}
.user-main .about.collapsed-info .details {
    background: rgba(255, 255, 255, 0.21);
    border-bottom: 1px solid #232323;
}
.category-list tbody tr{
    border-bottom: 1px solid #232323;
}
.user-main .about .secondary {
    background: #232323;
    border-top: 1px solid #232323;
    border-bottom: 1px solid #232323;
}
.user-main .user-content {
    background-color: #525252;
    border: 1px solid #232323;
}
div.ac-wrap {
    background-color: #222;
    border: 1px solid #232323;
}
.select2-drop-active,.list-controls .category-dropdown-menu {
    border: 1px solid #232323;
}
.select2-container.select2-container-active,.select2-dropdown-open a.select2-choice {
    border-color: #232323;
}
.select2-container-active {
    box-shadow: #232323 0px 0px 6px 0px;
}
body textarea:focus,body textarea {
    border-color: #FFF;
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.3),0 0 8px #FFF;
    background-color: #222;
    color: #FFF;
}
aside.quote .title,blockquote {
    border-left: 5px solid #414141;
}
.hljs-keyword, .javascript .hljs-title, .nginx .hljs-title, .hljs-subst, .hljs-request, .hljs-status {
    color: #08C;
}
aside.onebox {
    border: 5px solid #232323;
}
.topic-body,.topic-avatar {
    border-top: 1px solid #222;
}
a.mention, a.mention-group,.badge-card .badge-contents .badge-info {
    color: #C8C8C8;
    background: #414141;
}
.topic-map {
    background: #222;
    border: 1px solid #333;
}
.topic-map section {
    border-top: 1px solid #333;
    border-left: 1px solid #333;
}
.topic-map .buttons .btn:hover {
    color: #A7A7A7;
    background: #333;
}
.topic-map .buttons .btn {
    color: #a7a7a7;
    background: #222;
    border-left: 1px solid #222;
    border-top: 1px solid #333;
}
.timeline-container .topic-timeline .timeline-scrollarea {
    border-left: 2px solid;
    border-color: #222;
}
.badge-notification.clicks,p > code, li > code, pre > code,.badge-notification[href] {
    background-color: #414141;
    color: #e4e4e4;
}
.badge-notification.new-posts,.badge-notification.unread-posts {
    background-color: #222;
}
.topic-list > tbody > tr {
    border-bottom: 1px solid #232323;
}
.topic-list > tbody > tr:first-of-type,.category-list tbody tr:first-of-type {
    border-top: 3px solid #232323;
}
#topic-closing-info {
    border-top: 1px solid #232323;
}
#reply-control {
    background-color: #414141;
}
#topic-progress .bg {
    border-right: 1px solid #222;
    background-color: #222;
}
#topic-progress {
    background-color: #414141;
    border: 1px solid #414141;
    border-top: 0px solid #FFF0;
    border-left: 0px solid #FFF0;
}
control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview-header,#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview,#reply-control .d-editor-button-bar {
    background-color: #222;
    color: white;
}
.btn[href],.extra-info-wrapper .topic-link,.extra-info-wrapper .badge-wrapper.bullet span.badge-category{
    color: #414141;
}
.btn:hover {
    background: #222;
    color: #fff;
}
nav.post-controls button.d-hover,.btn {
    background: #222;
    color: #414141;
}
nav.post-controls button.like.d-hover {
    color: #fa6c8d;
    background: #222;
}
#user-card .card-content {
    background: rgba(34, 34, 34, 0.81);
}
#user-card h3 {
    color: #aeaeae;
}
#user-card.show-badges .badge-section .user-badge, #user-card.show-badges .badge-section .more-user-badges {
    background: #222;
    border: 1px solid #222;
    color: #414141;
}

#user-card.show-badges .badge-section .user-badge, #user-card.show-badges .badge-section .more-user-badges:hover {
    background: #222;
    border: 1px solid #222;
    color: #FFF;
}
.badge-card .badge-contents {
    background-color: #525252;
}
.top-sub-section li {
    border-left: #414141 solid 2px;
}
.stats-section li{
    background: #222;
    color: white;
}
.user-main .user-stream .item {
    padding: 20px 8px 15px 8px;
    background-color: #232323;
    border-bottom: 1px solid #333;
}
.moderator .topic-body {
    background-color: #222;
}
body {
    font-family: none;
}
}
