css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chiebukuro.yahoo.co.jp") {
  #wrapper {
      min-width: 950px;
      width: calc(100% - 20px);
      /*padding: 0px 0px 0px 0px;*/
  }
  #gyj_wrapper {
    min-width: 950px;
    width: 100%;
  }
  #main {
    min-width: 630px;
    width: calc(100% - 330px);
  }
  #main .mdTtl .ttl {
    width: 100%;
  }
  .shareBtnList ul {
    min-width: 630px;
    width: 100%;
  }
  .usrQstn {
    min-width: 540px;
    width: 100%;
  }
  .peopleList.twoColumn > li {
    min-width: 460px;
    width: 48%;
  }
  .peopleList > li > a {
    min-width: 320px;
    width: calc(100% - 140px);
  }
  .yj950-2 #wrapper {
    min-width: 950px;
    width: calc(100% - 20px);
  }
  body.yj950-2 #contents #contents-body #main-wrapper {
    min-width: 638px;
    width: calc(100% - 330px);
  }
  .yj950-2 #contents-body #main {
    width: 100%;
  }
  .generalRanking .clm2 .leftContents, .generalRanking .clm2 .rightContents {
    min-width: 465px;
    width: calc(50% - 10px);
  }
  .generalRanking .linkTxt a {
    padding: 10px 23px 10px 10px;
    background: url(http://i.yimg.jp/images/ks/pc/common/bg_link.png) no-repeat right center;
  }
  .generalRanking .clm3 .leftContents, .generalRanking .clm3 .centerContents, .generalRanking .clm3 .rightContents {
    min-width: 303px;
    width: calc(33% - 2px);
    margin: 0 5px 0 0;
  }
  .generalRanking .clm3 .linkTxt a, .questionList .more a, .questionListGrid .more a{
    background: url(http://i.yimg.jp/images/ks/pc/common/bg_link.png) no-repeat right center;
  }
  .listIcon .rankList ol li span.ttll {
    min-width: 350px;
    width: calc(100% - 100px);
  }

}
