css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habitica.com") {
  div.col-sm-3:nth-child(1) {
    width: 30%;
  }
  div.col-sm-3:nth-child(2) {
    width: 40%;
  }
  div.col-sm-3:nth-child(3){
    display: none;
  }
  div.col-sm-3:nth-child(4) {
    width: 30%;
  }
  div.col-sm-6:nth-child(1) {
    width: 30%;
  }
  div.col-sm-6:nth-child(2) {
    width: 40%;
  }
  div.col-sm-6:nth-child(3){
    display: none;
  }
  div.col-sm-6:nth-child(4) {
    width: 30%;
  }
}
