@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("volkskrant.nl") {

  body {
    background-image: none;
    background-color: #000;
    color: #AAA;
  }
  A { color: #44F; }
  A:visited { color: #22D; }
  A:active { color: #A00; }
  
  
  /* page header */
  header.page-header {
    background-color: #000;
  }
  
  /* action (menu/search) bar */
  div.sub-nav--wrapper, div.action-bar, div.mobile-nav__pane, ul.sub-nav {
    background-color: #666;
    color: #AAA;
  }
  
  /* around article list on homepage */
  div.container {
    background-color: #111;
  }
  
  /* homepage, left column (Nieuws); article list #1 and #2-n */
  div.ankeiler__wrapper-chapeau, article.ankeiler, h2.ankeiler__title {
    background-color: #000;
    color: #AAA;
  }
  
  /* homepage, right column (Uitgelicht) */
  div.ankeiler__wrapper, div.ankeiler__body, div.ankeiler {
    background-color: #000;
    color: #AAA;
  }
  
  
  /* article */
  div.article__main, div.article__wrapper--v2 {
    background-color: #000;
  }
  
  /* left/right background */
  div.page-main-content, div.page-content{
    background-color: #111;
  }
  
  /* area around main article */
  article.article--v2 {
    background-color: #000;
  }
  
  /* links/aanbevolen artikelen/meest gelezen panel */
  div.list-container, aside.article__aside { 
    background-color: #000;
  }
  
}
