css:
@-moz-document domain("youtube.com") {
body{
	background: #fff;
	/*overflow-x: hidden;*/
}

@keyframes ripple {
  0% {
    transform: scale(0,0);
    opacity: 1;
  }
  15% {
    transform: scale(6.5);
    opacity: 1;
  }
  95% {
    transform: scale(9);
  }
  100% {
    opacity: 0;
    transform: scale(9);
  }
}

@keyframes ripple2 {
  0% {
    transform: scale(0,0);
    opacity: 1;
  }
  15% {
    transform: scale(6.5);
    opacity: 1;
  }
  95% {
    transform: scale(9);
  }
  100% {
    opacity: 0;
    transform: scale(9);
  }
}

.yt-uix-form-input-checkbox-element:after,
.yt-uix-checkbox-on-off input[type=checkbox]+label .toggle:after,
#upload-btn:after,
#masthead-search .search-btn-component:after,
#yt-masthead-user #yt-masthead-account-picker .yt-masthead-user-icon:after,
#yt-masthead-user #sb-button-notify:after,
#watch8-sentiment-actions .like-button-renderer .yt-uix-button:after,
#appbar-guide-button:after {
  	content: '';
  	position: absolute;
  	top: calc(50% - 2.8px);
  	left: calc(50% - 2.8px);
  	width: 4.8px;
  	height: 4.8px;
  	background: rgba(0,0,0,0.3);
  	opacity: 0;
  	border-radius: 100%!Important;
  	margin: 0;
}

.yt-uix-form-input-checkbox-container:not(.checked) .yt-uix-form-input-checkbox-element:after,
.yt-uix-checkbox-on-off input[type=checkbox]:focus:not(:active)+label .toggle:after,
#upload-btn:focus:not(:active)::after,
#masthead-search .search-btn-component:focus:not(:active)::after,
#yt-masthead-user #yt-masthead-account-picker.yt-uix-clickcard-active .yt-masthead-user-icon:after,
#yt-masthead-user #sb-button-notify:focus:not(:active)::after,
#watch8-sentiment-actions .like-button-renderer .yt-uix-button:focus:not(:active)::after,
#appbar-guide-button:focus:not(:active)::after {
  animation: ripple .4s ease-out;
}

.yt-uix-form-input-checkbox-container.checked .yt-uix-form-input-checkbox-element:after{
	background: rgba(54, 100, 156, .26);
    animation: ripple2 .4s ease-out !Important;
}

.yt-uix-button {
    border: none;
    box-shadow: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    background: transparent;
    text-transform: uppercase;
    z-index: 0;
    font-weight: 500!important;
    height: 36px;
    font-size: 14px;
    padding: 0.7em 0.57em;
    outline: none!important;
	text-decoration: none !important;
}

.yt-uix-button.yt-uix-button-subscribe-branded, .yt-uix-button.yt-uix-button-subscribed-branded {
    padding: 0 16px;
    height: 36px;
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2)!important;
	transition: box-shadow 0.28s cubic-bezier(0.4, 0, 0.2, 1);
}

.yt-uix-button.yt-uix-button-subscribe-branded:active, .yt-uix-button.yt-uix-button-subscribed-branded:active {
    padding: 0 16px;
    height: 36px;
    box-shadow: 0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12), 0 5px 5px -3px rgba(0, 0, 0, 0.4)!important;
}

.yt-uix-button-subscribe-branded,.yt-uix-button-subscribe-branded:hover, .yt-uix-button-subscribe-branded[disabled], .yt-uix-button-subscribe-branded[disabled]:hover, .yt-uix-button-subscribe-branded[disabled]:active, .yt-uix-button-subscribe-branded[disabled]:focus {
    color: #fefefe;
    background-color: #e62117;
}

.unsubscribe-label,
.yt-uix-subscription-button:before{
	display: none!important;
    background: none;
}

.yt-uix-button-subscribed-branded.hover-enabled:hover .subscribed-label{
	display: inline !important;
}

.yt-uix-button-subscribe-branded .yt-uix-button-content, .yt-uix-button-subscribed-branded .yt-uix-button-content {
    font-size: 14px;
    font-weight: 500;
}

.yt-uix-button-subscribed-branded.hover-enabled:before {
    display: none;
}

.yt-uix-button-subscribed-branded.yt-uix-button,
.yt-uix-button-subscribed-branded{
	background: transparent;
    color: rgba(0, 0, 0, .54);
    border: 1px solid rgba(0, 0, 0, .2);
    box-shadow: none!important;
}

.yt-uix-button-subscribed-branded.fixed-width.hover-enabled:hover .subscribed-label{
	visibility: visible;
}

a{
	text-decoration: none!important;
}

.yt-subscription-button-subscriber-count-branded-horizontal {
    height: 36px;
    line-height: 36px;
    margin:0;
    border: none;
    background-color:transparent;
    font-size: 14px;
    color: rgba(0, 0, 0, .54);
    border-radius: 0;
    padding: 0 16px!important;
}

/* Toolbar */

#masthead-positioner{
    box-shadow: 0 4px 5px 0 rgba(0,0,0,0.14), 0 1px 10px 0 rgba(0,0,0,0.12), 0 2px 4px -1px rgba(0,0,0,0.2);
}

#yt-masthead-container {
    background: #fff;
    border-bottom: 0;
    min-width: 0;
    padding: 0 !important;
    position: relative;
    height: 64px;
    display: flex;
    justify-content: center;
    width: 100%;
}

#masthead-positioner-height-offset {
    height: 64px !important;
}

#yt-masthead {
    width: 100%!important;
    padding: 0 !important;
    display: flex;
	justify-content: space-between;
}

#masthead-search {
    margin-top: 0;
    max-width: 720px;
    flex: 0 1 50vw;
    overflow: hidden;
    padding: 0;
    position: relative;
    flex-grow: 1;
	pointer-events: all;
}

#yt-masthead-container:after,
#yt-masthead-container:before,
#masthead-appbar-container{
	display: none !Important;
}

#yt-masthead-content{
	order: 2;
	flex-grow: 2;
    display: flex;
    justify-content: center;
    pointer-events: none;
}

.gsib_a {
    padding: 0;
}

#masthead-search-terms {
    border: none !important;
    line-height: 48px;
    display: flex;
    margin: 8px 4px;
    background: rgba(0,0,0,0.04);
    box-shadow: none;
    border-radius: 2px;
    height: 48px;
    white-space: nowrap;
    transition: background linear 270ms,box-shadow linear 0ms 270ms;
    padding: 0 16px 0 72px;
}

#masthead-search-terms input::-webkit-input-placeholder {
  color: rgba(0,0,0,0.54);
}

#masthead-search-terms input::-moz-placeholder {
  color: rgba(0,0,0,0.54);
}

#masthead-search-terms input:-ms-input-placeholder { 
  color: rgba(0,0,0,0.54);
}

#masthead-search-terms input:-moz-placeholder {
  color: rgba(0,0,0,0.54);
}

.gsfe_a{
	box-shadow: none;
}

.gsfe_b {
	box-shadow: 0 2px 2px 0 rgba(0,0,0,0.14), 0 3px 1px -2px rgba(0,0,0,0.12), 0 1px 5px 0 rgba(0,0,0,0.2)!important;
    background: #fff !important;
}

.gstl_50.gssb_k{
	display: none !important;
}

#masthead-search-terms input{
    color: #222;
    line-height: 48px;
    padding: 0 16px 0 72px;
	font-size: 14px;
}

#masthead-search .search-btn-component {
    height: 40px;
    width: 40px;
    background: transparent;
    border: none;
    padding: 0;
    box-shadow: none;
    display: flex;
    justify-content: center;
    position: absolute;
    left: 16px;
    top: 12px;
    z-index: 999;
    border-radius: 50%;
}

#masthead-search .search-btn-component .yt-uix-button-content {
    opacity: .54 !important;
    background: no-repeat url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_search_black_18dp.png) 0 0;
    background-size: 24px 24px;
    width: 24px;
    height: 24px;
    box-shadow: none;
    margin: 0;
}

.gsst_b {
    height: 40px;
    width: 40px;
    padding: 0;
    border: none;
    top: 4px;
    box-shadow: none;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 999;
    border-radius: 50%;
}

.gsst_a {
    opacity: .54;
    background-position: 0 0;
	background-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/hardware/2x_web/ic_keyboard_black_18dp.png);
    background-size: 24px 24px;
    width: 24px;
    height: 24px;
    box-shadow: none;
    margin: 0;
    padding: 0;
}

.gsok_a {
	display: none;
}

.gstl_50.gssb_c{
    margin-top: 3px;
}

.gssb_m {
    color: #000;
    margin: 16px 0;
}

.gstl_50.gssb_c {
    display: flex!important;
    transform: scale(1,0);
    transform-origin: 50% 0;
    transition: transform .2s cubic-bezier(0.4, 0.0, 0.2, 1);
}

.gstl_50.gssb_c:not([style*="display: none"]) {
    transform: scale(1,1);
    opacity: 1;
}

.guide-pinned.show-guide .gstl_50.gssb_c {
	margin-left: 120px;
}

.gstl_50.gssb_c tbody {
    min-width: 100%;
}

.gstl_50.gssb_c tr {
	display: flex;
    min-width: 100%;
	justify-content: flex-start;
    align-items: center;
}

.gssb_e {
    border: none;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.14), 0 3px 1px -2px rgba(0,0,0,0.12), 0 1px 5px 0 rgba(0,0,0,0.2);
    border-radius: 0 0 2px 2px  !Important;
    position: relative;
    overflow: hidden;
    background-color: #fff;
}

.gstl_50.gssb_c:after {
    content: " ";
    position: absolute;
    top: -5px;
    z-index: 99999;
    left: 1px;
    right: 1px;
    background: #fff;
    height: 5px;
    border-bottom: 1px solid rgba(0,0,0,0.12);
}

.gssb_a {
    padding: 0 16px 0 72px;
	font-size: 14px;
    width: 100%;
}

.gssb_a, .gssb_a td {
    line-height: 40px;
    color: #212121;
}

.gssb_a td:first-child {
	margin-left: 0!important;
}

.gssb_a td:last-child {
	margin-left: auto;
}

a.gspqs_a {
    padding: 0 8px;
    text-decoration: none !Important;
    text-transform: uppercase;
    font-weight: 500;
    color: rgba(0,0,0,0.54);
    height: 32px;
    display: flex;
    align-items: center;
    cursor: pointer;
    border-radius: 2px;
    font-size: 13px;
}

/* Toolbar actions */

#yt-masthead-signin,
#yt-masthead-user{
	order: 3;
    color: #555;
    margin: 0!important;
    display: flex;
    align-items: center;
    padding: 0 16px;
}

.signin-container .yt-uix-button {
	color: #cd201f;
}

.signin-container .yt-uix-button:hover {
	background: rgba(0,0,0,0.12);
}

#upload-btn,
#yt-masthead-user #sb-button-notify, #yt-masthead-user #yt-masthead-notifications-button{
    margin: 0 8px 0 0;
    width: 40px;
    height: 40px;
    padding: 0;
    border-radius: 50%;
    display: flex;
	align-items: center;
    justify-content: center;
    overflow:initial;
    position: relative;
    opacity: 1;
}

#upload-btn .yt-uix-button-icon-wrapper{
	opacity: .54;
}

#yt-masthead-user .yt-uix-button-icon-bell {
    position: relative;
    top: 0;
	content: '';
    opacity: .54 !important;
    background-position: 0 0;
	background-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/social/2x_web/ic_notifications_none_black_18dp.png);
    background-size: 24px 24px!important;
    width: 24px!important;
    height: 24px!important;
    box-shadow: none;
}

#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell {
    opacity: .54 !important;
    background-position: 0 0;
	background-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/social/2x_web/ic_notifications_black_18dp.png);
}

#yt-masthead-user .sb-notif-on .yt-uix-button-content {
    background: #cb4437;
    border: 2px solid #fff;
    border-radius: 50px;
    left: 20px;
    line-height: 16px;
    min-width: 16px;
    font-size: 11px;
    opacity: 1;
    top: auto;
    bottom: 2px;
}

.yt-uix-button-icon-wrapper, .yt-uix-button-has-icon.no-icon-markup:before {
    margin-right: 0;
}

#yt-masthead-user .yt-masthead-user-icon {
    height: 40px;
    width: 40px;
    position:relative;
}

#yt-masthead-user .yt-thumb {
    background: transparent;
    height: 40px;
    width: 40px;
}

#yt-masthead-user .yt-uix-button img{
	border-radius: 50%;
    height: 40px;
    width: 40px;
}

/* Logo */

.skip-nav{
	display: none;
}

#yt-masthead .yt-masthead-logo-container {
    order: 1;
    margin: 0;
    padding: 0 0 0 16px;
    box-sizing: border-box;
    position: relative;
    height: 64px;
    display: flex;
    align-items: center;
}

#appbar-guide-button {
    display: flex;
    margin-left: 0;
    height: 40px;
    width: 40px;
    padding: 0;
    border: 0;
    border-radius: 50%;
    position: relative;
    overflow: initial;
    justify-content: center;
}

#yt-masthead-logo-fragment{
	background: #fff;
    height: 64px;
    width: 240px;
    position: absolute;
    left: 0;
    z-index: 9999;
    border-bottom: 1px solid rgba(0,0,0,0.12);
	border-right: 1px solid rgba(0,0,0,0.0);
    pointer-events: none;
    display: flex;
    overflow: hidden;
    align-items: center;
    box-sizing: border-box;
    padding: 0 24px 0 12px;
}

.exp-responsive #yt-masthead #logo-container {
    background: none;
}

.exp-responsive #yt-masthead #logo-container .logo, .exp-responsive #yt-masthead #logo-container .doodle, .exp-responsive #yt-masthead #logo-container .logo-red, .exp-responsive #yt-masthead #logo-container .content-region, .exp-responsive #ticker.has-arrow .ticker-righthand-content{
	display: flex !important;
}

.show-guide #yt-masthead-logo-fragment,
.show-guide #appbar-guide-menu {
    box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.15);
}

.guide-pinned.show-guide #yt-masthead-logo-fragment{
	border-right: 1px solid rgba(0,0,0,0.12);
    box-shadow: none;
}

#yt-masthead-logo-fragment:before{
	content: '';
    opacity: .54 !important;
	background-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/navigation/2x_web/ic_menu_black_18dp.png);
    background-repeat: no-repeat;
    background-size: 24px 24px;
    background-position: 4px 4px;
    width: 24px;
    height: 24px;
    box-shadow: none;
    margin: 0;
    padding: 4px;
}

#yt-masthead #logo-container {
    margin-right: 0;
    margin-left: 32px;
    display: flex!important;
    flex-direction: row;
    flex-wrap: nowrap;
}

#yt-masthead #logo-container span.content-region {
    position: relative;
}

#appbar-guide-button .yt-uix-button-icon-wrapper{
    opacity: .54 !important;
    background-position: 0 0;
	background-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/navigation/2x_web/ic_menu_black_18dp.png);
    background-size: 24px 24px;
    width: 24px;
    height: 24px;
    box-shadow: none;
    margin: 0;
}

.yt-uix-button-icon-appbar-guide {
    display: none !important;
}

/* Side nav */

.appbar-guide-menu-layout {
    margin-top: 64px;
}

.guide-pinned.show-guide .guide-pinning-enabled #page, .guide-pinned.show-guide .guide-pinning-enabled #appbar-content {
    width: calc(100% - 240px);
    padding-left: 0; 
}

#masthead-appbar,
#yt-masthead-content,
#content{
	transition: transform .35s cubic-bezier(0.24,1,0.32,1),visibility 0s!important;
    transform: translateX(0);
	will-change: transform;
}

.guide-pinned.show-guide #masthead-appbar,
.guide-pinned.show-guide #content{
    transform: translateX(240px);
}

.guide-pinned.show-guide #yt-masthead-content {
	transform: translateX(120px);
}

#appbar-content,
#page{
    padding-left: 0;
}

#yt-masthead-logo-fragment{
    opacity: 1;
	transition: transform .35s cubic-bezier(0.24,1,0.32,1),visibility 0s;
    transform: translateX(-101%);
}

#appbar-guide-menu {
    width: 240px;
    height: 100%;
    visibility: visible;
    left: 0!important;
    opacity: 1;
	transition: transform .35s cubic-bezier(0.24,1,0.32,1),visibility 0s;
    transform: translateX(-101%);
	will-change: transform;
    transition-delay: 0 !important;
    overflow-y: initial!important;
}

.guide-pinned #yt-masthead:before,
.guide-pinned #page-container:before{
	display: none;
}

#yt-masthead:before,
#page-container:before{
	content: "";
    position: fixed;
    height: 100%;
    width: 100%;
    z-index: 9;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    visibility: hidden;
    opacity: 0;
    transition: opacity ease-in-out 0.38s, visibility ease-in-out 0.38s;
    background-color: rgba(0, 0, 0, 0.3);
}

.show-guide #yt-masthead:before,
.show-guide #page-container:before{
    visibility: visible;
    opacity: 1;
}

.show-guide #yt-masthead-logo-fragment,
.show-guide #appbar-guide-menu {
    transform: translateX(0);
}

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: 1px solid rgba(0,0,0,0.12);
    box-shadow: none;
}

#guide-container {
    width: 100%;
    padding: 16px 0;
}

.guide-notification-item {
    max-height: 56px;
    overflow: hidden;
    position: relative;
}

.guide-item, .guide-view-more {
    display: flex !important;
    align-items: center;
    height: 48px;
    background: none;
    font-size: 14px;
    font-weight: 500;
    text-shadow: none !Important;
	color: rgba(0,0,0,0.87);
}

.guide-flyout-trigger.on-hover, .guide-item:hover, .guide-item:hover .yt-deemphasized-text, .guide-collapsible-item .yt-uix-expander-head:hover {
    background: rgba(0,0,0,0.1);
    color: rgba(0,0,0,0.87);
}

.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text {
    color: #cd201f;
    background: #f5f5f5!Important;
}

#guide-container .yt-thumb-20 img,
#guide-container .yt-thumb-20 {
    width: 24px;
    height: 24px;
}

.guide-item .thumb {
    margin-left: 16px;
    background: rgba(0,0,0,0.54)!Important;
    width: 24px!Important;
    height: 24px!Important;
    -webkit-mask-size: 24px 24px!Important;
}

.guide-item.guide-item-selected .thumb{
	background: #cd201f!Important;
}

.guide-what-to-watch-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_home_black_18dp.png)!Important;
    mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_home_black_18dp.png)!Important;
}

.guide-item .guide-my-channel-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/social/2x_web/ic_person_black_18dp.png)!Important;
    mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/social/2x_web/ic_person_black_18dp.png)!Important;
}

.guide-item .guide-trending-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/social/2x_web/ic_whatshot_black_18dp.png)!Important;
    mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/social/2x_web/ic_whatshot_black_18dp.png)!Important;
}

.guide-item .guide-my-subscriptions-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/av/2x_web/ic_subscriptions_black_18dp.png)!Important;
    mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/av/2x_web/ic_subscriptions_black_18dp.png)!Important;
}

.guide-item .guide-history-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_hourglass_full_black_18dp.png)!Important;
    mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_hourglass_full_black_18dp.png)!Important;
    
}

.guide-item .guide-watch-later-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_watch_later_black_18dp.png)!Important;
    mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_watch_later_black_18dp.png)!Important;
}

.guide-item .guide-playlists-icon {
    -webkit-mask-image: url(http://raw.githubusercontent.com/google/material-design-icons/master/av/2x_web/ic_playlist_play_black_18dp.png)!Important;
