.yt-spinner-img {
    display: inline-block;
    position: relative;
    width: 50%;
    height: 100%;
    overflow: hidden;
    border-color: inherit;
    background: none;
}

.yt-spinner-message {
    display: inline-block;
    position: relative;
    width: 50%;
    height: 100%;
    overflow: hidden;
    border-color: inherit;
}

.yt-spinner-img:after,
.yt-spinner-message:after {
    content: '';
    box-sizing: border-box;
    position: absolute;
    top: 0;
    border-width: 3px;
    border-color: inherit;
    border-radius: 50%;
	bottom: 0;
    width: 200%;
    border-style: solid;
    border-bottom-color: transparent !important;
    animation-duration: 1333ms;
    animation-timing-function: cubic-bezier(0.4, 0.0, 0.2, 1);
    animation-iteration-count: infinite;
}

.yt-spinner-img:after {
    left: 0;
    border-right-color: transparent !important;
    -webkit-transform: rotate(129deg);
    transform: rotate(129deg);
    animation-name: left-spin-paper-spinner-lite-1;
}

.yt-spinner-message:after {
    left: -100%;
    border-left-color: transparent !important;
    -webkit-transform: rotate(-129deg);
    transform: rotate(-129deg);
    animation-name: right-spin-paper-spinner-lite-1;
}

@keyframes left-spin-paper-spinner-lite-1 {
0% {
  transform: rotate(130deg)
}

50% {
  transform: rotate(-5deg)
}

to {
  transform: rotate(130deg)
}

}

@keyframes right-spin-paper-spinner-lite-1 {
0% {
  -webkit-transform: rotate(-130deg)
}

50% {
  -webkit-transform: rotate(5deg)
}

to {
  -webkit-transform: rotate(-130deg)
}

}

