css:
@-moz-document domain("discordapp.com") {
.wrapperDefault-1Dl4SS,
.icon-3tVJnl,
.icon-3dhTrw,
.guild-channels header,
.guilds-wrapper .btn-download-apps {
  display:none;
}
.guilds-wrapper {
  width: 51px;
}
.guilds-wrapper .guilds .guild.selected:before, .guilds-wrapper .guilds .guild.unread:before {
  left: -10px;
}
.guilds-wrapper .guilds {
  padding: 0 0 0 1px;
}
.guilds-wrapper .guilds .guild.unread:before,
.guilds-wrapper .guilds .guild.selected:before {
  border-radius: 0;
}
.channels-wrap {
  width: 150px;
}
.message-group.compact .timestamp {
  width: 45px;
}
.message-group, .chat .divider {
  margin-left: 0px;
  margin-right: 0px;
}
.message-group.compact .message .message-text .markup {
  text-indent: -50px;
  padding-left: 50px;
}
.guild-channels .channel-text a, .guild-channels .channel-voice {
  padding: 8px 0px 10px 10px;
}
.guild-channels .channel-text.unread:not(.selected):not(.channel-muted):before {
  opacity: .5;
}
.guild-inner {
  border-radius: 0 !important;
  opacity: .5;
}
.guild-header header {
    padding: 28px 4px;
}
.header-toolbar {
    margin-left: 0px;
}

}
