css:
@namespace url(http://www.w3.org/1999/xhtml);

/** cs-manager.com only: **/
@-moz-document domain(cs-manager.com) {


/**
CSM-pink theme:

   The colour values used - search and replace these to change the color theme.


	Page backgrounds:
#FFF8C6; Very light pink for the non-content-part of the page.
#FDD017; Light pink background color for the content part of the page and highligted tabs.
#FDD017; Very dark background. Used below left and right menus.


	Element backgrounds:
#FFBBBB; Pink background for elements on the page.
#EE8888; Darker pink background for emphasized elements.
#FFCCCC; Lighter pink bagground for larger textareas.
#FFE3E3; Very light background, used in forum.
#FF7A7A; Pink background for headers


	Borders:
#FDD017; Very dark pink used for all sorts of borders
#F87217; Dark pink used as border for headers to make them stand out
#F87431; Light pink used as border for headers to make them stand out


	Text:
#98AFC7; Standard text and menu links
#8AFB17; Link colors (except for menus)


**/


body {	background: #FBB917 !important;  /** not really used as bg is white **/
}

a {
	color: #8AFB17 !important;
/*	font-style: italic !important; */
}

/** Add remote logo as bg and change colors of header **/
#header {
	background-color: #FBB917 !important;
	background-image: url("http://www.sp-in.dk/csm/theme/pink/logo_pink.png") !important;
	background-repeat: no-repeat !important;
	border-right: 1px solid #8AFB17 !important;
	border-bottom: 1px solid #8AFB17 !important;
	border-left: none;
	border-top: none;
}


/** Hide original logo: **/
#header img {	visibility: hidden !important;} /** ingame **/
#header a img {	visibility: hidden !important;} /** front page **/


/** box containing it all and a few elements, that shouldnt have any specific colour **/
#container, #footer_container,.post,#content {
	color: #8AFB17 !important;
	background: #F87431 !important;
	border-right: 1px solid #DDBBBB !important;
	border-bottom: none; /** good manners to specify all borders **/
	border-left: none;
	border-top: none;
}

/** adding borders to some tables **/ 
table.rank,table.login {
	border: 1px solid #FDD017 !important; 
}
/** adding background to some tables **/ 
table.rank,table.login,table.mail,table.info {
	background: #FDD017 !important;
}
/** All sorts of 'small' elements needing colour **/
td.space,div.space {
	background: #FDD017 !important;
	border:none !important;
}
a.player,td.player,td.top,td.tactic,a.tactic {
	background: #FFCCCC !important;
	color: #8AFB17 !important;
}
a.tactic:hover {
	background: #FBB917 !important;
}
td.position {
	background: #FBB917 !important;
}

/** Adding borders and bg-colour to headers **/
td.head, h2, h3 {
	color: #8AFB17 !important;
	background-color: #FF7A7A !important;
	background-image: none !important;
	border-top: 2px solid #DD3333 !important;
	border-left: 2px solid #DD3333 !important;
	border-right: 2px solid #EEAAAA !important;
	border-bottom: 2px solid #EEAAAA !important;
}

/** All sorts of links. (tabs, menu etc) **/
li a {
	background: #FFBBBB !important;
	color: #8AFB17 !important;
	font-style: normal !important;
}

/** menu specific borders **/
ul#menu li a {
	border-bottom: 1px solid #DDBBBB !important;
	border-right: none !important;
	border-left: none !important;
	border-top: none !important;
}

/** tab-specific borders **/
ul#tab li a {
	border: 1px solid DDBBBB !important;
}

li a:hover {
	background: #EE8888 !important;
	font-style: normal !important;
}

/** Selected tabs **/
ul#tab li span {
	background: #FFDDDD !important;
	color: #000000 !important;
	font-style: normal !important;
	border-bottom: #FFDDDD !important;
}

/** other spans (eg online in freinds list) **/
li span {
	color: #000000 !important;
}

/** color of areas surrounding maillist **/
table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #EE8888 !important;
}

/** dark area below the left and right menus **/
#menu, #footer_left, #footer_right, div#container>div#info {
	background: #BB9999 !important;
}

/** search area **/
form#search {
	border-bottom: 1px solid #DDBBBB !important;
	background: #FFBBBB !important;
}

/**input fields and buttons **/
input,select,textarea,radio {
	background: #FFEEEE !important;
	border: 1px solid #DDBBBB !important;
}
.buttonsmall, .button {
	background: #FFEEEE !important;
	color: #000000 !important;
	border-top: 2px solid #DD3333 !important;
	border-left: 2px solid #DD3333 !important;
	border-right: 2px solid #EEAAAA !important;
	border-bottom: 2px solid #EEAAAA !important;
	font-size: 10px !important;
	padding: 0px !important;
}

/** news items (community news) **/
td.value {
	background: #FFCCCC !important;
}
td.foot,td.info {
	background: #FFBBBB !important;
}

/** Training room colours **/
#training_map,#training_player,#skill,#trainer,#training_players {
	background: #FFBBBB !important;
}
div#training_type {
	background: #EE8888 !important;
}

/** FORUM **/
tr.even {
	background: #FFE3E3 !important;
}
tr.odd {
	background: #FFEEEE !important;
}
div.modify {
	background: #FFEEEE !important;
}
}
