css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mozilla-russia.org") {

/* 	линки  */
*:not([id="header"]) a ,
*:not([id="nav-main"]) a	{
	color: #2A55D4  !important;  /*    009F00 */
}	
*:not([id="header"]) a:hover ,
*:not([id="nav-main"]) a:hover {
	color: #dC3000  !important; 
  text-decoration: underline  !important; 
}		
*:not([id="header"]) a:visited ,
*:not([id="nav-main"]) a:visited	{
	color: #762776 !important;    /*   858855  DDBFFF 7A6645      */
}		
*:not([id="header"]) a:visited:hover ,
*:not([id="nav-main"]) a:visited:hover	{
	color: #BF1093 !important;     /* A83971  FF4DAD */
}	

/* 	на тёмном  */
#header a , 
#nav-main a	{
	color: #fff  !important;  /*    009F00 */
}	
#header a:hover , 
#nav-main a:hover	{
	color: #FFFF00 !important; 
  text-decoration:  none   !important; 
}		
#header a:visited , 
#nav-main a:visited	{
	color: #fff !important;    /*     DDBFFF 7A6645      */
}		
#header a:visited:hover , 
#nav-main a:visited:hover 	{
	color: #FFFF00  !important;     /*   FF4DAD */
}		
  
}  /*   domain("mozilla-russia.org") -------------------- */





/*  *************** forum ****************************************  */

@-moz-document domain("forum.mozilla-russia.org") {

/* 	список тем  */
	#punviewforum #vf > DIV > DIV > TABLE > TBODY > TR:hover  {
 		outline: 2px solid #1BA455 !important; 
	}
	#punviewforum #vf > DIV > DIV > TABLE > TBODY > TR:hover td {
		background-color: rgba(255, 255, 255, 0.8) !important;    /*  91FFDA  FFFF66   F7EEDC */
	}	
	
	#punviewtopic * {
		border-radius: 0px !important; 
	}
	
	/* пост */ 
	#punviewtopic	[class^="blockpost"]  {
	}
	
	/* заголовок поста */ 
	#punviewtopic	[class^="blockpost"] h2  {
	}
	/* слева дата и время */
	#punviewtopic	[class^="blockpost"] h2 > SPAN > A  {
	}
	/* справа номер */
	/* #punviewtopic */	[class^="blockpost"] h2 > SPAN > SPAN.conr > A {
		font-weight: bold !important;
	}	

	/* низ поста  */ 
	/* #punviewtopic */	[class^="blockpost"] .postfootleft , 
	/* #punviewtopic */	[class^="blockpost"] .postfootright	a {
		font-weight: bold !important;		
	}	
	/* #punviewtopic */	[class^="blockpost"] .postfootleft .online {
		color: #007700 !important;
	}
	
	/* левая часть поста  */ 	
	/* #punviewtopic */	[class^="blockpost"]	.postleft * {
		font-family:  Verdana   !important; 
	}
	/* user name  */
	/* #punviewtopic */	[class^="blockpost"]	.postleft DT a {
		font-size: 1.15em !important; 
	}	
	
	
	/* непосредственно пост */ 
	#punviewtopic	[class^="blockpost"] .postmsg {
	}	

	
	
	/* код */ 
	/* #punviewtopic */	[class^="blockpost"] .codebox {
		background-color: #FaFEF8 ;
		border: 1px solid #C6D7B1 !important;
	}
	/* полоска Код: */
	/* #punviewtopic */	[class^="blockpost"] .codebox .incqbox > h4  {
    border: 1px solid #C6D7B1 !important; 
		background-position-x: 4px !important;
	}
	/* Выделить код */
	/* #punviewtopic */	[class^="blockpost"] .codebox .incqbox > a  {
		font-weight: bold !important;
		background-position-x: 0px !important;
    border-top: 1px solid #C6D7B1 !important; 
		border-right: 1px solid #C6D7B1 !important; 
	}	
	/* #punviewtopic */	[class^="blockpost"] .codebox .incqbox > h4 ,
	/* #punviewtopic */	[class^="blockpost"] .codebox .incqbox > a {
	  background-color: #E6FEDA !important; 
		padding:  7px 14px 7px 24px !important;
		background-position-y: 7px !important;
	}	
	
	
	
	
/* цитата */     
/* #punviewtopic */	[class^="blockpost"] .postmsg > BLOCKQUOTE {
    background-color: rgba(253, 252, 242, 1.0) ;    
	  border: 1px solid #E6CFA8 !important;
}     
BLOCKQUOTE > div > BLOCKQUOTE  {
    background-color:  hsla(48, 85%, 91%, 0.29)  !important; 
/*  background-color: rgba(235, 217, 200, 0.18) ;   */  
	  border: 1px solid #E6CFA8 !important;
}    	
	
	
	
	
	/* Добавлено ... */
	/* #punviewtopic */	[class^="blockpost"] .postmsg SPAN[style="color: #808080"] > EM > I {             
		text-decoration: underline  #3F7E7E ;
	}
	/* #punviewtopic */	[class^="blockpost"] .postmsg SPAN[style="color: #808080"] + * {
		margin: 0px  0px  6px  0px   !important;
	}
	/* Отредактировано ... */
	/* #punviewtopic */	[class^="blockpost"] .postmsg p.postedit   {
		margin-top: 8px !important;	
    margin-bottom: 0px !important;
		padding-top: 0px !important;
    padding-bottom: 0px !important;
	}
	/* #punviewtopic */	[class^="blockpost"] .postmsg SPAN[style="color: #808080"] > EM > I ,
	/* #punviewtopic */	[class^="blockpost"] .postmsg p.postedit > EM {
		font-weight: bold ;
		font-style: normal !important;   /*  normal, italic  oblique. */
		color: #3F7E7E !important;		
	}
	
	
	
	
	
	/* spoiler */
	/* #punviewtopic */	[class^="blockpost"] .postmsg	.spoiler {
		border: 1px solid #9CC5E2 !important;
	}
	/* открытый */
	/* #punviewtopic */ [class^="blockpost"] .postmsg	.spoiler .spoiler-minus:hover {
		color: #CC0000 ;
	}	
	/* закрытый */
	/* #punviewtopic */	[class^="blockpost"] .postmsg	.spoiler .spoiler-plus:hover {
		color: #009900 ;
	}	
	
	
	
/* отправка поста  */	
/* кнопки Отправить /  Предварительный просмотр	 */
#post > p > INPUT[type="submit"] {
		height: 36px !important;
		font-weight: bold ;
	  padding-left: 25px !important;
    padding-right: 25px !important;
}
/* кн. Отправить	 */	
#post > p > #submit {
	  padding-left: 140px !important;
    padding-right: 140px !important;
}	
/* мелкие псевдо   [ + ] [ -]   */
#post .bbincrement   {
		margin-top: 4px !important;
  	font-weight: bold ;
}	
#post .bbincrement > a   {
  padding-left: 6px !important;
  padding-right: 6px !important;
	color: #000 !important; 
}		
/* 	КНОПКИ  */
INPUT[type="submit"] ,
INPUT[type="button"] ,
#post .bbincrement > a 		{
	 border: 1px solid #A9B0C4 !important;
   border-radius: 0px !important; 
   background-image: -moz-linear-gradient(to bottom, 
        rgba(255, 255, 255, 0.5) 20% , rgba(55, 55, 55, 0.22) ) !important ; 
   background-color: rgba(240, 240, 255, 1.0)  !important;
   padding-top: 2px !important;
   padding-bottom: 2px !important;
   text-decoration:  none   !important; 
}
INPUT[type="submit"]:not([disabled="true"]):hover ,
INPUT[type="button"]:not([disabled="true"]):hover	, 
#post .bbincrement > a:not([disabled="true"]):hover 		{
   border-color: #91DA73 !important; 
   background-image: -moz-linear-gradient(to bottom, 
        rgba(255, 255, 255, 0.5) 20% , rgba(55, 55, 55, 0.22) ) !important ; 	
   background-color:  #DEFDD1 !important;
	 text-decoration:  none   !important; 
}  	
	
	
	
	
/* быстрый переход	 */
#qjump LABEL > 	SELECT  /* вып. меню   */     ,
#qjump LABEL > 	INPUT   /* кн. Перейти */ {
		height: 22px !important;
		font-weight: bold ;
}
	
	
	
	
	
	
	/* номера страниц */  
	p[class="postlink conr"] {
		  font-weight: bold !important;  
	}
	p[class="postlink conr"] > a {
      text-decoration:  none !important;  
      background-color: #fff ;
	}	
	p[class="postlink conr"] > STRONG {    	/* тек. */  
	    color: #fff !important;
      background-color: #6DA6F2 !important;
	}	
	p[class="postlink conr"] > a ,
	p[class="postlink conr"] > STRONG {
      border: 1px solid #6DA6F2  !important;
      padding: 2px 5px    !important;
      margin-left: 1px !important;
      margin-right: 0px !important;
	}	
	/* hover */
	p[class="postlink conr"] > a:hover , 
	p[class="postlink conr"] > a:visited:hover{
      text-decoration:  none !important;  
      background-color: #CA5100;
      border: 1px solid #CA5100 !important;
	    color: #fff !important;
}
	
	
}  /*   domain("forum.mozilla-russia.org") -------------------- */
