css:
@-moz-document domain("www.postgresql.org") {
html {
    background: #111;
}
#docHeader {
    background: linear-gradient(#111, #0064a5);
}
#docHeaderLogo img {
    display: none;
}
#docHeaderLogo:before {
    content: 'PostgreSQL ';
    color: #fff;
    font-size: 20pt;
    line-height: 3;
}
#docHeaderLogo:after {
    content: ' docs';
    color: #ccc;
    font-weight: bold;
    font-size: 12pt;
}
#docContainerWrap {
    background: #111;
    color: #ddd;
    font-size: 11pt;
    padding-top: 3em;
}
#docContainer {
    max-width: 1000px;
    padding: 3em;
    background: #333;
    box-shadow: 0 1em 2em #000;
    border-radius: 1em 1em 0 0;
}
#docContainer a {
    color: #008bb9 !important;
}
#docContainer a:visited {
    color: #f26522 !important;
}
#docSearch form label,
#docNav,
#docVersions {
    color: #999;
    text-shadow: 0 0 3px #000;
}
tt.COMMAND,
tt.LITERAL,
tt.REPLACEABLE,
tt.TYPE,
tt.FILENAME {
    font-size: 1em !important;
}
tt.COMMAND,
tt.LITERAL {
    padding: 1px 7px;
    background: #666;
    border-radius: 5px;
    color: #fff;
}
blockquote.NOTE,
blockquote.TIP,
table.CALSTABLE,
table.CAUTION,
pre.SYNOPSIS,
pre.PROGRAMLISTING {
    box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.5) !important;
    color: #111;
}
#docContainer table {
    margin-bottom: 2ex;
}
#docContainer h2, #docContainer h3 {
    color: #EC5800;
}
#docContainer hr {
    border: none;
    border-bottom: 2px solid #666;
}
#docSearch form #q {
    padding: 5px;
    border: none;
    border-radius: 9px 0 0 9px;
}
#docSearch form #submit {
    padding: 4px 6px;
    border-radius: 0 9px 9px 0;
}
}
