css:
@-moz-document regexp("https?://(encrypted|www).google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})/.*") {
/*
 * Name:    GOOGLE - Onna bugeisha (Female Samurai)
 * Author:  NORWIN  "https://userstyles.org/users/110629"
*/
body#gsr.hp, html[dir="rtl"] body#gsr.hp {background: #000000 url(/*[[backhp]]*/)no-repeat center bottom fixed!important;background-size:contain!important;}
body#gsr {background: #000 url(/*[[backsearch]]*/)no-repeat right bottom fixed!important;background-size:contain!important;} 
html[dir="rtl"] body#gsr {background: #000000 url(/*[[backsearch]]*/.gif)no-repeat left bottom fixed!important;background-size:contain!important;}
/***effekte***/
body#gsr.hp #body, body#gsr.hp .tsf-p{animation-name: fadeIn;-webkit-animation-name: fadeIn;animation-duration: 1s;-webkit-animation-duration: 1s;}
@keyframes fadeIn {0% {transform: scale(0);opacity: 0.0;}100% {transform: scale(1);opacity: 1;}}
@-webkit-keyframes fadeIn {0% {-webkit-transform: scale(0);opacity: 0.0;}100% {-webkit-transform: scale(1);opacity: 1;}}
#rcnt{animation-name: fadeIn;-webkit-animation-name: fadeIn;animation-duration: 0.8s;-webkit-animation-duration: 0.8s;}
@keyframes fadeIn {0% {transform: translateY(-100%);opacity: 0.0;}50% {transform: translateY(-20%);opacity: 0.0;}100% {transform: translateY(0%);opacity: 1;}}
@-webkit-keyframes fadeIn {0% {transform: translateY(-100%);opacity: 0.0;}50% {transform: translateY(-20%);opacity: 0.0;}100% {transform: translateY(0%);opacity: 1;}}
/***display none***/
#pmocntr2.pmoabs, #epbar, [aria-label="promo"], #taw, #_L8b, #cnsh, #prm{display:none!important}
/***backgrounds***/ 
#appbar, #appbar *:not(._W6g):not(._m7g), #cnt, .sfbgx,.sfbg,.sfbgg, #hdtb, #hdtbSum, #hdtb.hdtba #hdtbMenus, #fbar, #hdtb-tls:hover, .hdtb-tl, #rllhd__fldhc, ._Db:hover, .ksb, .THL2l, .iKjWAf{background: transparent!important;border:none!important;box-shadow:none!important;}
#hdtbMenus.hdtb-td-c {display: none;}
.mw *:not([style^="background:url(/images/nav_logo"]):not(.mn-dwn-arw):not(.action-menu-panel):not(._olf):not(.sr__color-square):not(span):not(.ktf):not(.lubh-bar):not(._hih) {background: transparent!important;border: none!important;box-shadow:none!important;} 
/***logo***/
[title="Google"], [src^="/images/branding/googlelogo/2x/googlelogo_color"], #logo{-webkit-filter: grayscale(100%) brightness(250%); filter: grayscale(100%) brightness(250%);}
/***links % text***/
body#gsr .s,body#gsr em,body#gsr span:not(.sbico),body#gsr p,body#gsr h3, body#gsr *{color:#fff!important;text-shadow:-0.5px 0 black, 0 0.5px black, 0.5px 0 black, 0 -0.5px black;}
body#gsr a,body#gsr .a,body#gsr cite,body#gsr cite a:link,body#gsr cite a:visited,body#gsr .cite,body#gsr .cite:link,body#gsr #_bGc > i,body#gsr .bc a:link,body#gsr ._xdb .fl,body#gsr ._tX {color:#bbb!important;}
body#gsr a:hover,body#gsr .a,body cite:hover,body#gsr cite a:link:hover,body#gsr cite a:visited:hover,body#gsr .cite:hover,body#gsr .cite:link:hover,body#gsr #_bGc > i:hover,body#gsr .bc a:link:hover,body#gsr ._xdb .fl:hover,body#gsr ._tX:hover {color: #999!important;}
/***footer nav bild***/
[style^="background:url(/images/nav_logo"] {-webkit-filter: grayscale(100%) brightness(200%); filter: grayscale(100%) brightness(200%);}
/***searchresu bilder***/
._Q4k,._olf{-webkit-filter: grayscale(100%) brightness(200%); filter: grayscale(100%) brightness(200%);}
/***menu bild***/  
#gbwa > DIV:first-child > a:first-child{filter:invert(1);-webkit-filter:invert(1)}
/***Google menu background & border***/  
body#gsr [aria-grabbed="false"]:hover a,body#gsr [aria-grabbed="false"]:hover a span:last-child {background: transparent!important;border:none!important;color: #bbb!important;}
#gbwa > DIV:nth-child(2) > span, #gbwa > DIV:nth-child(2) > a {border-bottom: none!important;width: auto!important;left: auto!important;}
#gbwa > DIV:first-child > DIV:nth-child(3){border-bottom-color:#333!important}
[aria-dropeffect="move"], [aria-hidden="false"]{background-color:#333333!important;opacity:0.95}
[aria-grabbed="false"] a{margin:7px 1px!important;}
/***searchbar***/
.sbibod *:not(.gsri_a), .kpbb, a.aciXEb{background: transparent!important;border: none!important;}
.sbibod, .jhp input[type="submit"], .mw input, .RNNXgb, .sbct.sbhl {background: rgba(255,255,255,0.2)!important;border: none!important;}
body#gsr .sbico {background: url(https://www.google.de/images/nav_logo242.png) no-repeat -107px -55px!important;}
body#gsr .gsri_a {-webkit-filter: contrast(0) brightness(3);filter: contrast(0) brightness(3)!important;}
.jhp input[type="submit"]:hover, .gbqfba {background: rgba(000,000,000,0.8)!important;border: none!important;}
[role="search"] .sbdd_b, .sbdd_a *, .aajZCb{background: #333333!important;border: none!important;}
.jsrp.mdm.minidiv .sfbg{background: #000!important;border: none!important;}
/***nav menu background***/
.hdtb-mn-o *:not([id^="sl"]), .hdtb-td-o, .hdtb-mn-c *{background: transparent!important;border: none!important;box-shadow:none!important;} 
g-menu, .hdtb-mn-o, .hdtb-mn-c, .action-menu-panel, #fsett, [class^="ab_dropdown"], [role="menu"] {background: #333333!important;border: none!important;}
.hdtbItm.hdtbSel{color:#888!important;}
#hdtb-msb .hdtb-mitem.hdtb-msel, #hdtb-msb .hdtb-mitem.hdtb-msel-pre{border-bottom: 3px solid #555!important;}
/***nav menu slider***/
#appbar .mR2gOd{width: calc(100% - 120px)!important;margin-left: 60px!important;}
#appbar ._ffj ._bfj{padding-left: 0px!important;padding-right: 0px!important;}
/***button anmelden & einstellung***/
[href^="https://accounts.google."]{background:#333333!important;border: none!important;color: #bbb!important;}
[id="abar_button_opt"]:not([class$="hdtb-dd-b"]){filter:invert(100%);-webkit-filter:invert(100%);border:none!important;background: #ccc!important;}
#abar_button_ss{background:none!important;border: none!important;}
}

@-moz-document regexp("https?://www.google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})/maps.*") {
/***maps***/
#gbwa > DIV:first-child > a:first-child{filter:invert(0);-webkit-filter:invert(0)}
.widget-consent-tiny, .widget-consent-small{display: none!important;}
.s, em, span:not(.sbico), p, h3, h2, h1, ul, li, div, button, label, input {color: #000!important;text-shadow: none!important;}
a, .a,body cite, cite a:link, cite a:visited, .cite,body .cite:link, #_bGc > i, .bc a:link, ._xdb .fl, ._tX, .blue-link{color:#4285F4!important;text-shadow: none!important;}
}

@-moz-document regexp("https?://ogs.google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})/.*") {
/***Google menu background & border***/ 
div{background-color:#333333!important;opacity:0.95;border:none!important;box-shadow:none!important}
a span:last-child, a{background-color: transparent!important;border:none!important;color: #eee!important;}
span{border-bottom-color:#444!important}
a {color: #bbb!important;}
li:hover a span, a:hover{color: #bbb!important}
a{margin:7px 1px!important;}
*{text-decoration:none!important}
}
