css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vid.me") {
  #index .splash,
  .header.opaque { 
    background: #333;  
  }
  
  .tabs-big-wrapper h1,
  #video_view .video_title,
  .videos_list > li .thumb_title_label,
  #video_view .video_suggestions .title > a,
  .header.opaque .search input,
  #video_view .video_description,
  body,
  #user_view .user_info2 .user_username a,
  .notifications_list li a:not(.btn),
  .tabs li.active a
  {
   color: #fff;
  }
  
  .videos_list .thumb_more > a img {
    display: none;
  }
  
  .videos_list .thumb_more > a:after {
    content: '\2026';
    font-size: 40px;
    display: block;
    top: -14px;
    position: relative;
  }
  
  body.pg-video-view, 
  body,
  .comments_list .comment_author_photo > a,
  .notification-dropdown
  {
   background-color: #111; 
  }
   
  .videos_list > li > .inner,
  #video_view .video_info2,
  #video_view .video_description_wrapper,
  #video_view .video_share,
  #video_view .video_info3,
  #video_view .video_likes, 
  #video_view .video_tips, 
  #video_view .comments,
  #video_view .video_nav,
  .device-desktop .gutter,
  #video_view .video_suggestions,
  #user_view > .inner,
  #user_view .user_actions a.user_follow.following
  {
   background-color: transparent; 
  }
  
  .comments_list .comment_author_username,
  #video_view .video_description a
  {
   color: #f82a53;
  }
  
  .comments_list .comment_date,
  .comments_list .comment_reply a {
    color: #999;
  }
  
  .js-comment-container .children {
    background: #0e0e0e;
  }
  
}
