css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sciencesetavenir.fr") {

  body{
    background-color: #333;
    
  }
  /* Menu */
  header menu ,header .header-top{
    background-color: #222;
  }
  header menu .menu .d-rbrq-on > a {
    color: #ccc;
    }
  header menu .dropdown > a:hover{
    color: #fff;
  }
  
  header .header-top .h-connect .abo{
    background-color: #222;
    color: #ccc;
  }
  .header-sticky menu .dropdown .dropdown-menu .abo a{
       background-color: #222;
    color: #ccc; 
  }
  header menu .dropdown .dropdown-menu .more ul{
    background-color: #222;   
  }
  header menu .dropdown .dropdown-menu .more ul li a{
    color: #ccc; 
    
  }
   /* end menu */
  
  /* Body */
  .highlights-top, .container,.container-in, header .header-banner {
    background-color: #333;
  }
  .container-main-article .content-main article .corps{
    color: gray;
  }
  .container-main-article .corps .theme-1{
    background-color: #222;
    
  }
  
  .content-right{
    background-color: #222;
  }
  .bloc.pub-300x600{
    visibility: hidden;
  }
  
  .container-home .author > a{
    color: #ccc;
  }
  /* Instant */
  
  .container-home .instant a{
    color: #ccc;
  }
  .container-home .instant a span{
    color: #fff;
    
  }
    /* Titles*/
    .container-home .item h2, .container-home .item h3{
      color: #4078c0;
    }
  
  /* end Body */
  
  /* Social buttons */
  header .header-top .h-social{
  	filter: invert(100%);
    
  }
  /* end Social buttons*/
  
  /* search button */
  header .header-top .h-search{
    background-position: 0 74.60%;
  }
  
  /* end search button*/
  
  /* Article share */
  .article-share div{
    background-color: #222;
    
  }

  .article-share div .print{
    background-color: #222;
    
  }
  .article-share div .print::before{
    opacity: 0.5;
    background-position:0 41.4%;
  }
   
  /* end Article share */
  
  /* Reaction */
  .lire-reactions .reactions::before{
    background-position: 0 71.1%;
  }
  /* end reaction */
  
  /* Buttons */ 
  /* Home*/
  header menu .menu .d-rbrq-home > a > .picto{
  	filter: invert(100%);
    
  }
  /* Video */
  .container-home .picto-play::after{
  	filter: invert(100%);
    
  }
  /* Author */
  .container-home .picto-auteur::after{
  	filter: invert(100%);
    
  }
  /* Eye */
  .container-home .picto-eye::after{
  	filter: invert(100%);
  }
  /* Submit */
  .container-main-recherche .form-search input[type="submit"]{
  	filter: invert(100%);
  }
  /* diaporama*/
  
  .bloc.article-diaporama .left-col h3::before, .content .content-right .bloc.article-diaporama .left-col strong::before
  {
  	filter: invert(100%);
    opacity: 0.7;
  }
  .container-home-rbrq .container-main-article .content-main .bloc-photos .txt::before{
  	filter: invert(100%);
    opacity: 0.7;
  }
  .container-home-rbrq .container-main-article .content-main .bloc-photos .video .txt::before{
    filter: none;
  }
  /* End buttons */
  
  h1,.article-chapo, .container-main-article .content-main article h3{
    color: #C0C0C0;
  }
  a{
    color: #4078c0;
  }
  a:visited, a:active{
    color: #305A8E;
  }
  
  /* Footer */
  
  .container-pre-footer .pre-footer-sm .title-big{
    color:#C0C0C0;
  }
  
  /* Tweeter */
  .SandboxRoot{
    color: #C0C0C0;
  }
    .EmbeddedTweet{
    background-color: #222 !important;;
    
  }
  .timeline-Widget{
    background-color: #222 !important;
  }
 /* End tweeter*/
  
  /* end Footer */
  
  /* */
  .container-home .container-2cols .lespluslus{
    background-color: #444;
  }
  /* */
  
  
  /* Hide cookie bar*/ 
  .cookies-mod{
    visibility: hidden;
  }
  /* end cookie*/
  
  
  /* Minimal menu*/
  
  .menu-xs{
    background-color: #222;
  }
  .menu-xs form{
    background-color: #222;
    
  }
  .menu-xs form input::-moz-placeholder{
    font-size: 20px;
  }
  
  /* */
  a:hover, #sidebar, #sidebar a{
    color: gray
  }
  
  .grid article, .post-grid article{
    color: gray
    
  }
  /* */
}
