css:
@-moz-document domain("4chan.org"), domain("4channel.org") {
/*background*/
 body {
	background: url(https://i.imgur.com/Fva1E6d.png);
	color: #000000;
	background-repeat: no-repeat;
	background-attachment: fixed;
	background-position: right top;
	background-size: cover;
}
/*navbar mobile*/
 div#boardNavMobile {
	padding: 2px 4px;
	background-color: #5f4e95;
	overflow: hidden;
	border-bottom: 0px solid #2c2c2c;
	position: fixed;
	top: 0px;
	left: 0px;
	right: 0px;
	font-size: 12px;
	z-index: 9001;
}
.boardSelect .customBoardList, .boardSelect .custom-menu-ctrl {
	color: #2c2c2c;
}
a, a:visited {
	color: #2c2c2c;
}
.pageJump a {
	color: #2c2c2c;
}
div.boardSelect>strong {
	padding-right: 5px;
	color: #000000;
}
div.pagelist {
	background: #2c2c2c;
}
a:hover {
	color: #000000;
}
div.boardBanner {
	text-align: center;
	clear: both;
	color: #5f4e95;
}
a:hover {
	color: #5f4e95!important;
}
/*persistent nav*/
 .persistentNav, div#boardNavMobile {
	padding: 2px 4px;
	background-color: #5f4e95;
	overflow: hidden;
	border-bottom: 0px solid #5f4e95;
	position: fixed;
	top: 0px;
	left: 0px;
	right: 0px;
	font-size: 12px;
	z-index: 9001;
}
/*desktop nav*/
 div#boardNavDesktop {
	font-size: 9pt;
	color: #2c2c2c;
	display: block;
}
/*settings box*/
 #settingsMenu > div {
	top: 25px;
	vertical-align: top;
	max-height: 85%;
	background: #ffffff;
	border: 1px solid #5f4e95;
}
.panelHeader {
	border-bottom: 1px solid #5f4e95;
}
/*please stop adblock*/
 body > div.center, body > form > div.center {
	display:none !important;
}
.click-me {
	display:none !important;
}
option:hover {
	color: #a385ff;
}
}

@-moz-document domain("4chan.org"), domain("4channel.org") {
/*the boards*/
 div.reply {
	background-color: rgba(0, 0, 0, 0);
	border: 0px solid #5f4e95;
	border-left: none;
	border-top: none;
	border-bottom: none;
	border-right: none;
	display: table;
	padding: 2px;
}
div.post blockquote.postMessage {
	display: block;
	background-color: #ffffff;
	border: 1px solid #5f4e95;
}
div.postInfo {
	display: block;
	width: 100%;
	background-color: rgba(0, 0, 0, 0);
}
.burichan_new .backlink a, .yotsuba_b_new .backlink a {
	color: #5f4e95 !important;
}
div.sideArrows {
	color: #5f4e95;
	float: left;
	margin-right: 2px;
	margin-top: 0px;
	margin-left: 2px;
}
hr {
	border: none;
	border-top: 1px solid #5f4e95;
	height: 0;
}
.quoteLink, .quotelink, .deadlink {
	color: #664697!important;
	text-decoration: underline;
}
blockquote>span.quote {
	color: #664697;
}
div.pagelist {
	background: #ffffff;
	border: 1px solid #5f4e95;
}
.postblock {
	background-color: #a385ff;
	color: #000000;
	font-weight: bold;
	border: 1px solid #000000;
	padding: 0 5px;
	font-size: 10pt;
}
.extButton {
	background: url(http://i.imgur.com/GCRIsXn.png);
}
.style {
	background: url(http://i.imgur.com/GCRIsXn.png);
}
div.extButton {
	background: url(http://i.imgur.com/GCRIsXn.png);
}
.pageJump a:hover {
	color: #5f4e95!important;
}
div.post div.postInfo span.postNum a {
	text-decoration: none;
	color: #000000;
}
.yotsuba_new .postMenuBtn, .futaba_new .postMenuBtn {
	color: #000000;
}
a.replylink:not(:hover), div#absbot a:not(:hover) {
	color: #34345C!important;
}
table.postForm>tbody>tr>td:first-child {
	background-color: #98E;
	color: #000;
	font-weight: bold;
	border: 1px solid #000;
	padding: 0 5px;
	font-size: 10pt;
}
.reply:target, .reply.highlight {
	background: #D6BAD0!important;
	border: 1px solid #BA9DBF!important;
	border-left: none!important;
	border-top: none!important;
	padding: 2px;
}
div.post div.postInfo span.postNum a {
	text-decoration: none;
	color: #34345C!important;
}
.preview {
	background-color: #D6DAF0;
	border: 0px solid #dd0000!important;
	border-right-width: 0px!important;
	border-bottom-width: 0px!important;
}
.panel {
	background-color: #fff;
	border: 1px solid #5f4e95
}
textarea {
	border: 1px solid #5f4e95;
}
#disclaimer-dialog .hd {
	background: #5f4e95;
	color: #000000;
}
.globalMessage {
	color: #5f4e95;
}
}

@-moz-document url("https://www.4chan.org/"), url("https://www.4chan.org/feedback"), domain("4chan.org"), domain("4channel.org") {
/*4chan home*/
html {
    background: rgb(0 0 0 / 0%);
}
 .boxbar {
	background: #5f4e95;
	color: #000000;
	background-repeat: no-repeat;
	background-attachment: fixed;
	background-position: right top;
	background-size: cover;
}
#gbqfwapq {
	width: 0px;
}
#gbqfwa {
	height: 0px;
}
#boards a {
	color: #000000;
}
#boards a {
	color: #000000;
	text-decoration: none;
}
.warning {
	color: #a385ff;
}
.c-thread img {
	border: 1px solid #5f4e95 !important;
}
a:visited {
	color: #2c2c2c;
}
keygen, select, select[size="0"], select[size="1"] {
	border-radius: 0px;
	border-color: #a385ff;
}
user agent stylesheet select {
	background-color: #a385ff;
}
#boards a:hover {
	color: #664697;
	text-decoration: underline;
}
.dd-menu ul {
	background-color: #a385ff;
	border: 1px solid #a385ff;
	border-right-width: 2px;
	list-style: none;
	padding: 0;
	margin: 0;
	white-space: nowrap;
}
.dd-menu li {
	cursor: pointer;
	position: relative;
	padding: 2px 4px;
	vertical-align: middle;
	border-bottom: 1px solid #a385ff;
}
.dd-menu {
	position: absolute;
	font-size: 12px;
	line-height: 1.2em;
}
.dd-menu li:hover {
	background-color: #664697;
}
#ft li.current, #ft li.fill {
	background: #ffe;
	color: #000000;
}
.c-thread img {
	border: 1px solid #5f4e95;
	display: inline;
}
#ft li a {
	color: #000000;
	text-decoration: none;
}
#ft li a:hover {
	color: #c9cde8;
}
#ft li {
	background: #a385ff;
}
a, a:visited {
	color: #000000;
}
element.style {
	display: inline-block;
	width: 300px;
	height: 0px;
	margin: 0 10px;
}
body > div#doc > div[style="text-align:center;margin: 10px 0"] {
	display: none !important;
}
#fp-menu li:hover {
	background: #a385ff;
}
#filter-btn:hover, #opts-btn:hover {
	cursor: pointer;
	color: #a385ff;
}
body > dive#doc >div[style="width:300px;height:0px;margin:10px auto 10px auto"] {
	display: none !important
}
.warning {
	color: #a385ff;
}
#entries th {
	border: 1px solid #000000;
	background-color: #5f4e95;
}
#entries td {
	border: 1px solid #000000;
}
footer li {
	background-color: #a385ff;
}
input:focus, select:focus, textarea:focus {
	outline: none;
	border: 1px solid #a385ff;
}
#announce .boxbar {
	background: #5f4e95;
	color: #000000;
}
.closebutton {
	background: url(http://i.imgur.com/GCRIsXn.png);
}
}

@-moz-document url("https://www.4chan.org/blotter"), url-prefix("https://www.4chan.org/4channews") {
/*4chan Blotter*/
 #title {
	color: #5f4e95;
}
.redtxt {
	color: #664697;
}
/*boarders*/
 #entries tr {
	border-bottom: 1px solid #000000;
}
#entries {
	border: 1px solid #000000;
	border-top: 1px solid #000000;
	border-bottom: 1px solid #000000;
	border-left: 1px solid #000000;
	border-right: 1px solid #000000;
}
#entries th {
	background-color: #5f4e95;
	color: #000000;
	border-left: 1px solid #000000;
}
#entries td {
	border-left: 1px solid #000000;
}
/*footer*/
 footer li {
	background-color: #a385ff;
}
footer li a {
	color: #000000;
}
#ft li a:hover {
	color: #c9cde8;
}
footer li a:hover {
	color: #c9cde8;
}
/*4chan news*/
 .boxbar {
	background: #5f4e95;
	color: #000000;
}
.content h3 {
	background: none;
	border: 1px solid #5f4e95;
	color: #000000;
	font-size: 116%;
	padding: .5em;
}
#ft li a {
	color: #000000;
	text-decoration: none;
}
#ft li a:hover {
	color: #c9cde8;
}
#ft li {
	background: #a385ff;
}
#ft li.current, #ft li.fill {
	background: #ffe;
	color: #000000;
}
}

@-moz-document url("https://www.4chan.org/rules"), url("https://www.4chan.org/pass"), url("https://www.4chan.org/banned"), url-prefix("https://www.4chan.org/faq"), domain("4chan.org") {
/* FAQ */
 .boxbar {
	background: #5f4e95;
	color: #000000;
}
.boxcontent a {
	color: #2c2c2c;
}
.left-box .boxbar {
	background: #5f4e95;
	color: #000000;
}
.right-box .boxbar {
	background: #5f4e95;
	color: #000000;
}
a, a:visited {
	color: #000000;
}
.left-box {
	background: #fff;
	color: #000000;
}
.boxcontent>ul {
	color: #000;
}
.right-box {
	background: #fff;
	color: #000;
}
dt {
	color: #664697;
}
dt.first {
	color: #664697;
}
#ft li {
	background: #a385ff;
}
#ft li a {
	color: #000;
}
#ft li.current, #ft li.fill {
	color: #000000;
}
element.style {
	background: #5f4e95;
}
hr {
	background: #000000 !important;
}
#logo {
	background: url(http://i.imgur.com/v5yznRb.png) top left no-repeat;
}
.postb {
	background-color: #a385ff;
	color: #000000;
	font-weight: bold;
	border: 1px solid #000;
	padding: 0 5px;
	font-size: 10pt;
}
}
