css:
@-moz-document url-prefix("https://connect.sfu.ca/") {
.overview{background-color: #1C1C1F;}
tbody{background-color: #1C1C1F}
.mainbox{color: #1C1C1F}
.ZhRowOdd TD,body{background-color: #1C1C1F}
.ZhRow TD,.ZhSubTabs{background-color: #1C1C1F}
.MsgBody,TD .folder{background-color: #1C1C1F!important;}
.msgbody{color: #ADAEB3}
a{color: #ED2828}
div{color: #ADAEB3;background-color: #1C1C1F!important;}
h1{color: #ADAEB3}
h2{color: #ADAEB3}
p{color: #ADAEB3}
.msghdrname,.msghdrvalue{color: #ADAEB3}
TD{color: #00FFED}
.TbTop INPUT, .TbBottom INPUT{color: #ED2828}
span{color: #ED2828}
.List TH,.Tree TH,.Displayimages{background-color: #09A309}
.ZhTISelected,.AppTabs .AppTabSelected,.RowSelected TD {background-color: #0955A0!important;}
textarea, input,#subjectField,.ZhAppViewContent{background-color: #303438;}
.user_font_system, .user_font_system TEXTAREA, .user_font_system INPUT{color: #DDD9D9}
.ZhSubTabs .TabSelected{background-color: #ADADAD}
.ZhCalDayHour{background-color: #303438}
.ZhCalDayGrid A:link, .ZhCalDayGrid A:visited, .ZhCalMonthTable A:link, .ZhCalMonthTable A:visited{color: #ED2828}

}
