css:
@namespace url(http://www.w3.org/1999/xhtml);
/* 记得启动 turn on 样式 */

@-moz-document
  /* 单一 URL */
  url("http://www.weather.com.cn/weather40d/101200101.shtml"),
  /* 带有相同前缀的一系列 URL */
  url-prefix("http://www.weather.com.cn/weather"){
  
  
  /*　禁止内容出现 */
    
  /* 右侧栏 */
  .right,
  /* 顶部导航栏 */
    .weather_li,
    .topad_bg,
  /* 周边地区的天气 */
    #around,
  /* 高清图集 */
    .hdImgs,
  /* 近期重大天气事件 */
    .greatEvent,
  /* footer */
    .footer,
  /* 注释 */
    .explain,
  /* 意见建议 */
    #ab_yjfk,
    #ab_yjjy,
    #cyEmoji,
    /* 广告 */
    #adposter_6122,
    #adposter_6298,
    #adposter_6299,
    /* hot */
    #someDayNav > li:nth-child(4) > span:nth-child(2){
    display: none !important;
  }
    
    
    /* 美化 */
    
    /* header */
    .search-box{
      float: left !important;;
    }
    .w_li_logo{
      float: right;
    }
    /* 之所以不能水平居中的原因，正是这个 div 的宽度设置太大 */
    .con{
      border: 1px solid LightGrey;
      box-shadow: 0px 0px 20px 2px rgba(0, 0, 0, 0.1);
      border-radius: 10px;
      margin-top: 10px;
      margin-bottom: 40px !important;
      width: 700px !important;
    }
    
    /* 底部居中置顶固定预测时期选项栏 */
    #someDayNav{
      position: fixed;
      bottom: 0;
      right: 0;
      background-color: #3C3C41;
      z-index: 2 !important;
      width: 310px !important;
      border: 1px;
      border-radius: 10px;
    }
    #table td{
      z-index: 0;
    }
    #someDayNav a{
      color: white;
      text-decoration: none;
      padding: 5px;
    }
    #someDayNav a:hover{
      background-color: #00BF00;
      cursor: pointer;
      border: none;
      border-radius: 5px;
    }
    
    /* 内容栏 */
    .con .left .cnav li.on{
      background-color: #00BF00;
      cursor: pointer;
      border: none;
      border-radius: 5px;
    }
    .con .left .cnav li {
      background-color: #3C3C41;
      display: inline;
      color: #252525;
      cursor: pointer;
      float: left;
      height: 32px;
      line-height: 32px;
      margin-right: 2px;
      text-align: center;
      width: 60px !important;
      position: relative;
      border-radius: 10px;
}
    .left{
      margin-bottom: 15px;
      margin-top: 10px;
      margin-left:  10px;
    }
    .con .left .cnav li a{
      display:  inline;
    }
    .con .left .cnav li:hover {
      background-color: #00BF00;   
}
    .c7d ul.t {
      margin-left: 8px !important;
}
}
