css:
@-moz-document domain("zhihu.com") {
.zhi .zm-editable-content,
.zhi .zu-main-content .zm-item-rich-text,
.zhi .zh-summary.summary,
.post-body .post-content,
.ContentItem-content {
	font-size: 20px !important;
	font-family: "微软雅黑" !important;
	line-height: 2 !important;
}
.zh-summary.summary.clearfix {
	overflow-x: hidden;
}
.topic-pages .zu-top-feed-list .feed-item h2,
.topic-feed-page .zu-top-feed-list .feed-item h2,
h2.feed-title {
	font-size: 20px !important;
}
.topic-pages .zu-top-feed-list .feed-item h2 a:hover,
.topic-feed-page .zu-top-feed-list .feed-item h2 a:hover,
h2.feed-title a:hover {
	text-decoration: none;
	color: #F44336
}
.zu-main-sidebar > *,
.zu-main-sidebar .zm-side-section .zh-question-followers-sidebar,
.FeedbackButton-button-3waL,
.feed-main .feed-source,
.entry-body .zm-item-answer-author-info,
.entry-body .author-info.summary-wrapper,
#zh-home-list-title,
.HomeEntry-avatar,
.feed-item.feed-item-hook.actioncard,
#SidebarSignFlow,
.AdblockBanner,
.AppHeader.is-hidden .PageHeader,
.Question-sideColumn,.Profile-sideColumn, .Modal-backdrop {
	display: none !important;
}
.zu-main-sidebar > .zm-side-section:first-child {
	display: block !important
}
.AppHeader.is-hidden .AppHeader-inner {
	-webkit-transform: none !important;
	transform: none !important;
}
.zu-main-content .zu-main-content-inner {
	margin-right: 0 !important;
}
.zh-backtotop .btn-action {
	position: fixed;
	right: 5px;
	bottom: 100px;
}
.feed-item-inner {
	padding-top: 20px !important;
	padding-bottom: 30px !important;
	padding-left: 10px;
	padding-right: 10px;
}
.feed-content h2 {
	margin-bottom: 10px !important;
}
.topic-feed-page .feed-main .feed-content,
.page-explore .feed-item {
	padding: 15px !important;
}
.feed-item {
	background: #fafafa;
	padding: 0 !important;
	margin: 30px auto !important;
	border: 0 !important;
	transition: 0.2s all;
	box-shadow: 0 0 6px rgba(0, 0, 0, .16);
}
.feed-item .post-content h2,
.feed-item .post-content h1 {
	font-size: 2em;
}
.feed-content .entry-body h3 {
	font-size: 1.8em;
}
.zm-item-answer.zm-item-expanded {
	padding: 10px 30px !important;
	box-shadow: 0 0 2px rgba(0, 0, 0, .16);
}
.zm-item-answer.zm-item-expanded:hover {
	box-shadow: 0 0 6px rgba(0, 0, 0, .2);
}
.zm-item-answer.zm-item-expanded .zm-votebar {
	margin-left: -75px;
}
.feed-item:hover {
	box-shadow: 0 0 10px rgba(0, 0, 0, 0.16);
	background: #fff;
}
.zg-noti-number {
	background: #9e9e9e;
	border: 0;
}
.PushNotifications-count{
	background:#607D8B
}
a.zu-main-feed-fresh-button,
a.zu-main-feed-fresh-button:hover {
	background: #f7f7f7;
	border: 0;
}
.HomeEntry {
	margin-bottom: 0;
	border: 0;
	margin-top: -15px;
}
.HomeEntry-box {
	margin-left: 0;
}
.zu-main-feed-con {
	border: 0;
}
blockquote {
	line-height: 1.8;
	color: #666;
	box-shadow: 1px 1px 4px rgba(0, 0, 0, .1);
}
.zu-top {
	box-shadow: none;
	background: #0767c8 !important;
}
.zm-item .pin-body .summary div,
.feed-item .pin-body .summary div,
.zm-item .pin-body .summary blockquote,
.feed-item .pin-body .summary blockquote {
	max-height: 80px;
}
.feed-content[data-za-module=PinItem] {
	margin-top: -15px !important;
}
.feed-content[data-za-module=PinItem] .zm-item-vote {
	margin-top: 50px !important;
}
.zm-item-answer-detail {
	padding: 15px;
}
.feed-item[data-type=pin] .js-collapse-body button.up {
	margin-top: 30px
}
.awesome-answer-list .zm-item-meta .zm-meta-panel {
	padding: 15px
}
.js-topic-feed-page .topic-title.js-topic-title {
	margin-bottom: -20px
}
#zh-question-answer-wrap .zm-item-answer {
	margin-bottom: 30px;
}
.zhi .zu-main-content-inner .zh-answers-title {
	width: 400px;
	margin: 10px auto;
	text-align: center;
	background: #607d8b;
	transition: 0.2s all;
}
.zhi .zu-main-content-inner .zh-answers-title:hover {
	box-shadow: 2px 2px 6px rgba(0, 0, 0, .5);
	background: #e91e63;
}
.zhi .zu-main-content-inner .zh-answers-title .zg-link-litblue {
	color: #ffffff;
	display: block;
	text-decoration: none;
}
#zh-single-question-page .zh-answers-title {
	width: auto;
	margin: auto;
	text-align: left;
	background: transparent;
}
#zh-single-question-page .zh-answers-title:hover {
	box-shadow: none;
	background: transparent;
}
.item-collapse.is-sticky,
.feed-item.feed-item-hook.question-item {
	padding: 8px 12px !important;
}
.Question-mainColumn,.Profile-mainColumn {
	width: 100%
}
.AppHeader {
	background: #0767c8 !important;
}
.AppHeader-navItem {
	color: #fff !important
}
.Icon.Icon--logo {
	fill: rgb(255, 255, 235) !important
}
.Card {
	background: transparent !important;
	box-shadow: none;
	border: 0;
}
.Card .List-item, .Card > .QuestionAnswer-content {
	margin: 20px auto  !important;
	background: #fff  !important;
	padding:20px  !important;
	box-shadow:1px 1px 6px rgba(0,0,0,.16)
 }
.Card .ContentItem-meta{padding-top:10px !important}
.Card .ContentItem-actions{margin:0 !important;padding-left:0}
a.QuestionMainAction{color:#03A9F4;font-size:1.5em;}

.ContentItem-actions > span {position:absolute;left:-80px;top:20px;}
.ContentItem-actions.is-fixed > span{position:fixed;left:50%;top:80px;margin-left:-580px}
.ContentItem-actions > span  .VoteButton {display:block;margin-bottom:10px;padding:0;width:80px;height:40px;text-align:center;border-radius:10px 0 0 10px;}
.ContentItem-actions > span   .VoteButton--down{margin-left:30px;width:50px;padding-right:0;}
.SearchBar-mask .Mask-mask::before {
    content: "输入要搜索的内容按回车";
    text-align: center;
    font-size: 4em;
    margin-top: 157px;
    display: block;
    color: #fff;
}
.Modal-wrapper{left:50% !important;width:800px !important;margin-left:-400px !important;}
html{overflow-y:auto !important;margin-right:0 !important;}
body,.QuestionHeader,.QuestionHeader-footer{background:#f7f7f7;}
.Icon.Modal-closeIcon.Icon--remove{font-size:3em;width:30px !important;height:30px !important;color:#f00;fill:#f00}
}
