css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ebrun.com") {

body{
	background: #FBFBFB;
}
.newspaper-main .newspaper-content p
{
	border: 1px solid #e0e0e0;
	background: #f0f0f0;
	border-radius: 1px;
	padding: 25px;
	transition: all 450ms cubic-bezier(0.01, 0.48, 0.47, 0.99) 0ms;
	box-shadow: 5px 5px 10px 0 rgba(0, 0, 0, 0.05);
}

.newspaper-content p:hover{
	border: 1px solid #0062b0;
}

.newspaper-main .newspaper-content h2,
.newspaper-main .newspaper-content h4{
    margin-top:40px;
}
.newspaper-content h2 a:hover,
.newspaper-content h4 a:hover{
    color:#0181e6;
}
}
