css:
@-moz-document url-prefix("http://baidu.com"), url-prefix("http://www.baidu.com"), url-prefix("https://www.baidu.com"), url-prefix("https://baidu.com") {
.c-container {
    border: 1px solid #F5F5F5;
    border-radius: 1px;
    padding: 25px;
    transition: all 450ms cubic-bezier(0.01, 0.48, 0.47, 0.99) 0ms;
    box-shadow: 5px 5px 10px 0 rgba(0, 0, 0, 0.05);
}
.c-container:hover {
    border: 1px solid #B9D5FF;
    box-shadow: 5px 5px 10px 0 rgba(0, 0, 0, 0.10);
}
#content_right{
	border-left: none;
}
.c-gap-top{
	margin-bottom:20px;
}
.cr-title{
	margin-bottom:25px;
}
.opr-recommends-merge-panel {
    margin-bottom: 40px;
}
}
