css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cwa.me.uk") {

  html > body #rap{
    width: 100%
  }
  
  #header{
    padding: 0px;
    background-size:cover;
    height: 100px
  } 
  #header h1{
    margin: 20px;
  }
  .header2{
    border-bottom:5px solid #630
  }
  body{
    background: #222;
    color: #ccc;
  }
  #main{
    background: #333;
    width:100%;
    max-width: 800px;
   justify-content:  center;
    margin: 5px 0px 0px 50px;
  }  
  #sidebar{
    background: #333;
        border: 1px solid gray;
    float:right
  }
  #rap{
    background: transparent;
  }
  #sidebar form, #sidebar .block, #sidebar ul{
    background: transparent;
  }
 div#sidebar div.block p img{
    display:none;
  }
  #main ul li{
    margin: 0;
	padding: 6px 20px 6px 36px;
	list-style: none;
	background-image: url(http://www.fancyicons.com/free-icons/112/office-space/png/256/cup_coffee_256.png);
	background-repeat: no-repeat;
	background-position: left center;
	background-size: 30px;
  }
  
  .post{
    background: url(http://www.fancyicons.com/free-icons/177/vintage-kitchen/png/256/mug_256.png) no-repeat top left;
	background-size: 78px;
  }
  
  a:hover, a:active {  
    background: transparent; 
text-decoration: none;
    color: #a66349
}
  
  /* Comment */
  textarea{
    padding: 5px;
    color: #ccc;
    background: #333;
  }
  
  input{
        color: #ccc;
    background: #333;
  }
  /* End Comment */
}
