css:
@-moz-document url(https://osbs.gettodone.com) {


div.gtd_card {
    box-shadow: none;
    border-radius: 3px;
}

div.gtd_board_purple, td.gtd_board_purple {
    background: #8640AC;
}

div.gtd_board_lightgray, td.gtd_board_lightgray {
    background: #909090;
}

div.gtd_board_yellow, td.gtd_board_yellow {
    background: #D7CF3E;
}

div.gtd_board_black, td.gtd_board_black {
    background: #505050;
}

div.gtd_board_green, td.gtd_board_green {
    background: #338A5F;
}
div.gtd_board_blue, td.gtd_board_blue {
    background: #3D6AA6
}

#gtd_header {
    background-image: initial;
    box-shadow: none;
}

#gtd_footer {
    background: #3D6AA6;
}

div.cardspan {
    box-shadow: none;
    border-radius: 2px;
}

div.gtd_cardmini {
    box-shadow: none;
    border-radius: 2px;
    border: none;
}

.thick_left {
    border-left: 3px solid black;
}
.thick_top {
    border-top: 3px solid #000000;
}
.thick_bottom {
    border-top: 3px solid #000000;
}
.thick_right {
    border-top: 3px solid #000000;
}
a.glyphbutton {
    box-shadow: none;
}

a.glyphbutton, 
a.glyphbutton:visited {
    background-image: initial;
    border-radius: 2px;
}
a.glyphbutton:hover{
    background-image: initial;
}

.header_table_left_wide .glyphbutton {
    margin-left: 4px;
}

.gtd_board_header {
    height: initial !important;
}

.gtd_board_header h3 {
    margin-bottom: 0;
}

.gtd_board_footer {
    display: none;
}

.gtd_card {
    height: initial !important;
}

.gtd_card {
    min-height: initial !important;
}

.gtd_board_team img {
    border:  none !important;
    border-radius: 51px;
    height: 45px;
}
.inline_edit {
    position: absolute;
    right: 16px;
    top: 4px;
}

.card-list-view div.gtd_storymini .card_title h3.gtd_description {
    width: 170px !important;
}

div.gtd_cardmini div.card_title h3.gtd_description {
    width: 195px;
}

div.gtd_card.gtd_user {
    float: left;
    position: relative;
    width: auto;
    background: none;
    border: none;
}

.gtd_generic_single_container {
    margin-left: 50px !important;
    width: 20% !important;
    float: left !important;
}

.gtd_double_board_container {
    margin-right: 50px !important;
    width: 70% !important;
    float: right;
    position: relative;
}

#page-wrap gtd_generic_single_container + .clear {
    clear: none !important;
}

#page-wrap .gtd_generic_single_container + .clear {
    clear: initial !important;
}

div.gtd_user .lefthalf h3 {
    color: black !important;
}

.edit_toolbar {
    width: inherit;
}

.edit_toolbar .righthalf {
    clear: both !important;
    float: none;
    margin-top: 20px !Important;
    position: initial;
    line-height: 53px;
}
#footer{
	clear: both;
}
  
}
