css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Max-height divided by line-height gives you 
the number of lines that'll show without a
scrollbar. If the paragraph or text block goes
into more lines than that, use the overflow
to keep it small but show the scrollbar.

*/

@-moz-document domain("justthetalk.com") {

  /* Thread header */
div[class="headertext"] {
  overflow-y: auto;
  max-height: 6em;
  line-height: 1.2em;
    }

  /* Entire content of post - 12 lines or less = no scrollbar */
.postbody {
  overflow-y: auto;
  max-height: 14.4em;
  line-height: 1.2em;
    }
  
  /* Code block */
.postbody div[style*="margin-left: 36px;margin-right: 36px;"] {
  overflow-y: auto;
  max-height: 6em;
  line-height: 1.2em;
    }
  
  /* Code block */
.postbody div[class="panel-body"] {
  overflow-y: auto;
  max-height: 6em;
  line-height: 1.2em;
    }
  
 /* Centred text block */ 
.postbody p[class="post-centre"] {
  overflow-y: auto;
  max-height: 4.8em;
  line-height: 1.2em;
    }
  
 /* Block quoted paragraphs */ 
.postbody p[class="post-quoted"] {
  overflow-y: auto;
  max-height: 4.8em;
  line-height: 1.2em;
    }
  
 /* Indented paragraphs */ 
.postbody p[style*="margin-left:"] {
  overflow-y: auto;
  max-height: 6em;
  line-height: 1.2em;
    }
 


 /* Italic paragraphs */ 
.postbody p span.post-italic {
  display: inline-block;
  overflow-y: auto;
  max-height: 4.8em;
  line-height: 1.2em;
    }    
}
