css:
@-moz-document domain("watch2gether.com") {
:root {
    --acc: /*[[accC]]*/;
    --accd: rgba(225, 255, 255, .5);
}

a.bottom-sticker.feedback-sticker,
a.bottom-sticker.chromeext-sticker.chrome-extension-install,
#client-pusher > div.topbar > div > div > div > div.six.wide.computer.tablet.only.column.logo-column > div,
#client-pusher > div.topbar > div > div > div > div.ten.wide.right.aligned.inverted.computer.tablet.only.column > div > div:nth-child(2),
#client-pusher > div.topbar > div > div > div > div.ten.wide.right.aligned.inverted.computer.tablet.only.column > div > a,
#client-pusher > div.topbar > div > div > div > div.ten.wide.right.aligned.inverted.computer.tablet.only.column > div > div:nth-child(5),
#w2g-greatrow,
#player-settings-section > a,
#time-slider > div > div.noUi-origin > div:before,
#time-slider > div > div.noUi-origin > div:after,
#main-volume-slider > div > div.noUi-origin > div:before,
#main-volume-slider > div > div.noUi-origin > div:after,
#client-pusher > div.footer {
    display: none;
}

.topbar {
    background: #212121;
    border: none;
    box-shadow: 0px 0px 15px -1px rgba(0, 0, 0, .5);
    z-index: 999;
    position: relative;
}

.ui.toggle.checkbox input:checked ~ .box:before,
.ui.toggle.checkbox input:checked ~ label:before,
.noUi-connect {
    background-color: var(--acc) !important;
}

.w2g-yellow-bg,
.card.smartcard.available > div.content,
.card.smartcard.available > div.content>a,
.usermsg .ui.pointing.label,
.ui.dropdown .menu .selected.item,
.ui.dropdown.selected,
label:before,
.autocomplete-suggestion {
    background-color: var(--acc) !important;
    color: rgba(255, 255, 255, 0.9) !important;
}

#chat-input-field,
#chat-input-form > div > div,
.ui.primary.button,
.ui.primary.buttons .button,
#search-bar-input,
#search-bar-form > div > div > i,
#search-bar-form > div > div > button,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown,
#playlist-container > div.ui.fluid.action.input > input[type="text"],
#playlist-container > div.ui.fluid.action.input > button,
#playlist-container > div.ui.fluid.action.input > div.change-playlist-button.ui.icon.button {
    background-color: #212121 !important;
    color: #fff;
}

#chat-messages-table > div > div > div > div.chat-message-time {
    color: var(--acc) !important;
}

.card.smartcard.available,
#player-overflow,
.usermsg .ui.pointing.label,
#playlist-history > div.ui.bottom.attached.segment,
.main-content .ui.menu .active.item,
#chat-input-form > div,
#search-container,
#search-results-grid > div,
#userbar,
.ui.dropdown .menu .selected.item,
.ui.dropdown.selected,
#player-chat.fixed,
#search-bar-form,
#playlist-container > div.ui.fluid.action.input {
    box-shadow: 0px 0px 15px -1px rgba(0, 0, 0, .5) !important;
    /*     position: inherit;
    z-index: 5; */
}

#userbar {
    border-radius: 5px;
}

#client-container > div.ui.stackable.grid > div:nth-child(3),
#search-results-grid > div,
#player-settings-section > div.volume-dropdown.ui.icon.bottom.right.pointing.dropdown.item.active.visible > div {
    background-color: rgba(33, 33, 33, 0.7);
}

#playlist-shuffle > label,
#chat-messages-table > div.they.usermsg > div.chat-avatar > div,
#search-results-grid > div *,
.ui.selection.visible.dropdown>.text:not(.default) {
    color: #fff !important;
}

.main-content .ui.menu .active.item {
    box-shadow: 0 2px 0 var(--acc) inset;
}

#player-room-actions > span,
#player-video-info > b > a {
    color: var(--acc);
}

#player-room-actions > span {
    color: var(--accd);
}

#player-video-info,
#player-menu > div.ui.attached.inverted.small.menu,
#search-results-grid > div > div.extra.content > div {
    border-radius: 0;
    border: none;
    background-color: #212121 !important;
}

#playlist-history > div.ui.bottom.attached.segment,
#search-container,
.main-content .ui.menu .active.item,
#playlist-history > div.ui.top.attached.tabular.small.menu {
    background-color: rgba(33, 33, 33, .2) !important;
    border-radius: 0;
    border: none;
}

#chat-messages,
#client-container,
#chat-input,
#chat-input-field,
#search-results-menu,
#search-bar-input,
#search-results-grid > div,
#search-results-grid > div > div.image.mod-disable-clear,
#search-results-grid > div > div.content.search-video-content,
#search-results-grid > div > div.price,
#userbar,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown,
.ui.dropdown .menu .selected.item,
.ui.dropdown.selected,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active.visible > div.menu.transition.visible,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active > div.menu.transition.animating.slide.down.in,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.visible > div.menu.transition.visible.animating.slide.down.out,
.autocomplete-suggestions {
    border: none !important;
}

.autocomplete-suggestions::-webkit-scrollbar-track {
    background-color: #212121;
    box-shadow: none;
    border-radius: 0;
}

.autocomplete-suggestions::-webkit-scrollbar {
    width: 5px;
    background-color: #FFF;
}

.autocomplete-suggestions::-webkit-scrollbar-thumb {
    background-color: #FFF;
    box-shadow: none;
}

#chat-input {
    padding: 0;
}

#search-bar-input {
    padding: .67857143em 1em;
}

#chat-input-field,
#chat-input-form > div > div,
#search-bar-input,
#search-bar-form > div > div > div,
#search-results-grid > div,
#search-results-grid > div > div.image.mod-disable-clear > img,
#userbar,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active.visible > div.menu.transition.visible,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.active > div.menu.transition.animating.slide.down.in,
#playlist-container > div.ui.fluid.action.input > div.ui.fluid.compact.selection.dropdown.visible > div.menu.transition.visible.animating.slide.down.out,
#playlist-container > div.ui.fluid.action.input > input[type="text"],
#playlist-container > div.ui.fluid.action.input > div.change-playlist-button.ui.icon.button {
    border-radius: 0;
}

#search-bar-form > div > div > div {
    margin-left: -7.5px;
}

#playlist-container > div.ui.basic.segment {
    background: transparent !important;
}

#gfycat > i {
    filter: invert(1);
}

.main-content .ui.menu .item,
#player-chat {
    transition: .3s ease-in-out;
}

.main-content .ui.menu .active.item {
    padding: 0 25px !important;
}

body > div.autocomplete-suggestions {
    max-height: 150px !important;
}

#player-chat {
    transform: scale(1) !important;
    border: none !important;
    box-shadow: none !important;
}

#client-pusher > div.topbar > div > div > div > div.six.wide.computer.tablet.only.column.logo-column > a > div {
    background-image: url(https://i.imgur.com/OCeCsK3.png);
}

#time-slider > div > div.noUi-origin > div,
#main-volume-slider > div > div.noUi-origin > div {
    border-radius: 100000px;
}

.ui.menu .ui.dropdown .menu>.item {
    padding: 1em 2em !important;
}

.noUi-vertical .noUi-handle {
    width: 28px;
    height: 28px;
}
}
