/* Version */
/* <2017-01-07 Sat 10:36:53> */
/* 内容、图片自适应大小*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
    url-prefix("http://www.linuxidc.com/Linux/"),
    url-prefix("http://www.linuxidc.com/linux/") {
    
    
    /* 禁用一些内容 */ 
    body #tracqfloater,
    body div table tbody tr[align="center"],
    body div #navbar,
    body div #navsub,
    body div #footer,
    #printBody div.linub,
    #bodyTd.mm div div a img,
    #bodyTd.mm div div.linuc,
    html body div.twidth div.linus,
    body div table tbody tr td[class="linuz"],
    body div table tbody tr td .lframe,
    body div table tbody tr td .mframe,
    .mm div[align="center"] p[align="center"],
    #content p a[href="http://www.linuxidc.com"],
    body div table tr td[valign="top"],
    div.linux801:nth-child(5),
    .bdsharebuttonbox,
    #topUser,
    .topBar,
    div.twidth:nth-child(3) > table:nth-child(8) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(3) > div:nth-child(1) > div:nth-child(3),
    .linux3,
    .linux808,
    .linux801,
        /* 阅读新闻 */
        span.tt:nth-child(2){
        display:none !important;
    }
    body div table tbody tr td .mframe:first-child {
        display:inline !important;
    }
    
    
    /* 一些样式修改 */    
    /* 翻页和目录 */
    #printBody > a:nth-child(5),
    div.twidth:nth-child(3) > table:nth-child(8) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(3) > div:nth-child(1) > a:nth-child(6),
    div.mframe:nth-child(1) > div:nth-child(4) > div:nth-child(3) > a:nth-child(7) {
        position: fixed;
        right: 1.5em;
        bottom:5em;
        background: #FFF;
        border: 1px solid  #e1e1e8;
        border-radius:10px;
        box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
        text-align: left;
        /* ensure doesn't flow off the screen when expanded */
        max-height: 100%;
        overflow: auto;
        }
        /* body */ 
    #bodyTd{
        font-family: Helvetica, Arial, Vernada, Tahoma, STFangsong, STXihei, "Songti SC", "Microsoft YaHei", Heiti, sans-serif;
        font-size: 20px;
        margin: 1%;
        padding: 3%;
        line-height: 150%;
        border: 1px solid LightGrey;
        box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
        border-radius:10px;
    }
    /* 内容 */
    div.twidth:nth-child(3) {
        width: 100%;
        margin-top: 30px;
        }
   /* 图片 */
   #content img {
       border: 0;
       vertical-align: middle;
       text-align: center;
       width: 66.6%;
       /* 水平居中 */
       margin-left: auto;
       display: block;
       margin-right: auto;
        }
        /* 顶部固定栏 */
        .mframe .tm {            
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            display: block;
            background: #3C3C41;
            padding-top: 5px;
            padding-bottom: 5px;
            color: white;
        }
        .moreR {
            float: left;
            font-size: 16px;
            margin-left: 10px;
        }
        .moreR img {
            width: 10px !important;;
            height: 10px !important;
        }
    /* 字体 大中小 调整 */
    #printBody > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3),
    #printBody > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3){
        position: fixed;
        top: 0;
        right: 0;
        text-align: center;
        /* ensure doesn't flow off the screen when expanded */
        max-height: 100%;
        overflow: auto;
        font-size: 16px;
        z-index: 1;
        color: white;
        }
        #printBody > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > a:link, a:visited {
     color: white;
    text-decoration: none;
}
}
