css:
@-moz-document url-prefix("https://www.3dmgame.com/"){
#index_bg_box,
.tonglona_2,
.tonglona_3,
.tonglona_4,
.Tonglan,
#index_top_tonglan{
   display:none;
}
}
@-moz-document regexp("(https://www.3dmgame.com/)((news)|(vote))+.+"){
body{
    background:#e5e5e5;
}
.content{
    margin-top:0px!important;
    background: #e5e5e5;
}
.commentswrap,
.fotter,
.nav,
.pin,
.header_top,
.search,
.crumb,
.header_wrap
{
    display:none;
}
.news_warp_center{
	margin:30px 0 0 0;
}
.news_warp_center p{
	font-size:18px;
	line-height:35px;
}
.news_warp_top .bt{
	font-size:28px;
	text-shadow: 4px 4px 8px #bdbdbd;
	margin:10px 0 50px 0;
}
.time{
	margin-left:80px;
}
.Content_L {
	margin-left:173px;
	margin-top:80px;
}
	.Content_L:before{
		display: inline-block;
		content: '';
		width: 235px;
		height: 57px;
		margin-bottom: 50px;
		background:url("https://www.3dmgame.com/newpage/images/logo.png") no-repeat;
	}


.Content_R,
.dj_warp_a,
.dj_warp_b,
.dj_warp_c,
.dj_warp_d,
.dj_warp_e,
.dj_warp_f,
div[style='width:346px;height:288px; overflow: hidden;  margin-bottom: 20px;'],
div[style='width:346px;height:226px; overflow: hidden;  margin-bottom: 20px;']{
	display:none;
}

}
