css:
@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com"), domain("partner.steamgames.com") {
/* Node background */
.guide.subSections,
#highlightContent.workshopItemDescription,
.game_area_description,
.landingLeftCol,
.review_area,
.review_box .rightcol, .review_box .shortcol,
.rightcol .review_box.short,
.review_box.partial .content .gradient,
.forum_op,
.commentthread_comment_content {
	background: #f9f9f9;
}
/* Node text color */
.guide.subSections,
#highlightContent.workshopItemDescription,
.game_area_description,
.game_area_description ul,
.game_area_description ul strong,
#game_area_description h2,
.landingLeftCol, .landingLeftCol p, .landingLeftCol b, .landingLeftCol code, .landingLeftCol pre,
.AdminPageContent strong,
.review_area_content,
.review_box .content,
.review_box .shortcol .content,
.review_box .vote_header .title,
.review_box .vote_header .hours,
.review_box .short_header,
.review_box .postedDate,
.review_box .control_block .text,
.commentthread_comment_text,
.forum_op .topic, .forum_op .content,
.forumtopic_comments .commentthread_comment_text,
.dynamiclink_content, .dynamiclink_type, .dynamiclink_author {
	color: black;
}
/* Link colors */
.guide .detailBox a,
.game_area_description a,
.landingLeftCol a,
.review_area_content a,
.review_box .rightcol a,
.forum_op_author,
.forum_op .content a, .forum_op .content a:hover,
.commentthread_comment_text a, .commentthread_comment_text a:hover,
.forumtopic_comments .commentthread_comment_text a, .forumtopic_comments .commentthread_comment_text a:hover,
.commentthread_comment_author a,
.dynamiclink_url a, .dynamiclink_authorname, .dynamiclink_name,
#highlightContent.workshopItemDescription a {
	color: #1982d1;
}
/* Input backgrounds */
#post_announcement_form .gray_bevel,
.forumtopic_reply_entry .gray_bevel,
.forum_newtopic_input_area .gray_bevel,
.commentthread_entry_quotebox,
.commentthread_comment_edit_textarea_ctn {
	background: white;
}
/* Input text colors */
#post_announcement_form input,
#post_announcement_form textarea,
.forum_newtopic_input_area input,
.forum_newtopic_input_area textarea,
textarea.forumtopic_reply_textarea,
.commentthread_entry_quotebox textarea,
.commentthread_comment_edit_textarea_ctn textarea{
	color: black;
}
/* Padding to cope with now-different backgrounds */
.review_area_content,
.guide.subSections,
#highlightContent.workshopItemDescription,
.game_area_description,
.landingLeftCol {
	padding-left: 5px;
	padding-right: 5px;
}
.guide .detailBox {
	background-image: none;	
}
.review_box .rightcol {
	margin-left: 9px;
	padding-left: 5px;
}
.review_box.partial .content .gradient {
	opacity: 0.9;
	text-align: center;
}
.review_box.partial .content .gradient::before {
	content: ". . .";
	line-height: 30px;
}
.review_box a.vote_header, .review_box a.vote_header:hover,
.review_box .short_header {
	background: #e3e3e3;
}
.rightcol .review_box.short {
	opacity: 1;
}
.review_box .vote_header .review_source,
.review_box .short_header .review_source {
	opacity: 1;
}

/* BB: */
/*.subSectionTitle, div.bb_h1 {
	color: black;
	font-weight: bold;
	line-height: 1.5;
	border-bottom: 1px solid #ccc;
}*/
/* Comments: */
.commentthread_comment_content {
	padding: 2px 4px 2px 4px;
	margin: -2px 0 -2px -4px;
}
a.commentthread_author_valve, a.commentthread_author_developer,
.commentthread_workshop_authorbadge {
	color: #60d070;
}
.forumtopic_comments .commentthread_comment .commentthread_comment_author {
	opacity: 1;
	filter: none;
}
/* Dynamic link boxes (screenshots, etc): */
.dynamiclink_box {
	background: white;
	box-shadow: 0 0 3px 0 rgba(0, 0, 0, 0.1);
	border-color: #aaa;
}
/* technical documentation */
.landingLeftCol code, .landingLeftCol pre {
	font-family: Consolas, monospace;
	font-size: 100%;
}
.landingLeftCol pre {
	overflow-x: auto;
}
}
