css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("discord.js.org") {
    #docs-viewer, body {
        background: #21252b;
    }
    #home,
    #docs-body {
        background: #21252b;
    }
    #docs-navbar,
    footer,
    #app {
        background: hsl(220, 13%, 18%) !important;
    }
    #docs-navbar * {
        background: hsl(220, 13%, 18%) !important;
    }
    header {
        background: #21252b;
    }
    select {
        border: none !important;
    }
    ul li {
        color: hsl(208, 88%, 48%) !important;
    }
    li ul li {
        color: #999999 !important;
    }
    pre,
    code {
        background: hsl(220, 13%, 18%) !important;
    }
    code {
        color: hsl(220, 14%, 71%) !important;
    }
    h1,
    h2,
    h3 {
        color: #AAAAAA !important;
    }
    p {
        color: hsl(220, 14%, 71%) !important;
    }
    #class-overview .col .title {
        color: hsl(208, 88%, 48%) !important;
    }
    #class-overview .col ul li a {
        color: hsl(220, 14%, 71%) !important;
        border-left-color: hsl(220, 9%, 55%);
        ;
    }
    #class-overview .col ul li a:hover {
        color: hsl(220, 14%, 71%) !important;
        border-left-color: hsl(208, 88%, 48%);
        background: hsl(220, 13%, 18%);
    }
    #docs-visibility {
        color: hsl(220, 14%, 71%) !important;
    }
    #docs-sidebar {
        border-right-color: hsl(220, 14%, 71%) !important;
    }
    .router-link-active {
        background: hsl(220, 13%, 18%) !important;
    }
    #docs-sidebar-content ul li ul li a:hover {
        color: hsl(220, 14%, 71%) !important;
        border-left-color: hsl(208, 88%, 48%) !important;
        background: hsl(220, 13%, 18%) !important;
    }
    .hljs-keyword {
        color: hsl(286, 60%, 67%);
    }
    .hljs-built_in {
        color: hsl(187, 47%, 55%);
    }
    .hljs-string {
        color: hsl( 95, 38%, 62%);
    }
    .hljs-subst {
        color: hsl(355, 65%, 65%);
    }
    .hljs-comment {
        color: hsl(220, 9%, 55%);
    }
    .param-table {
        border: none;
    }
    .param-table thead tr th,
    .badge {
        background-color: hsl(219, 79%, 66%);
        color: #EEEEEE;
    }
    .param-table tbody tr td {
        background-color: hsl(220, 13%, 18%);
        color: hsl(220, 14%, 71%);
    }
    .class-item h3 {
        color: hsl(220, 14%, 100%);
    }
    .method-param {
        color: hsl(220, 14%, 71%);
    }
    .class-item .class-item-details {
        border-left-color: hsl(220, 14%, 71%) !important;
    }
    .warn {
        color: hsl( 42, 88%, 36%) !important;
        background-color: hsl( 36, 60%, 72%) !important;
        border-top: none !important;
        border-right: none !important;
        border-bottom: none !important;
    }
    .info {
        color: #7faeff !important;
        background-color: hsl(208, 88%, 48%) !important;
        border-top: none !important;
        border-right: none !important;
        border-bottom: none !important;
    }
}
